INSERT INTO `posts` (`id`, `title`, `slug`, `image`, `content`, `status`, `created_at`, `updated_at`, `category_id`, `user_id`) VALUES
(5, 'Vì sao hàng ngàn người tập trung trước Nhà hát lớn Hà Nội khiến đường tắc từ sáng tới chiều?', 'vi-sao-hang-ngan-nguoi-tep-trung-truoc-nha-hat-lon-ha-noi-khien-auong-tac-tu-sang-toi-chieu', 'jjwjv-1732441142797930947806.jpg', '<h2 class=\"detail-sapo\" data-role=\"sapo\">Từ s&aacute;ng tới chiều 24-11, tại trước khu vực Nh&agrave; h&aacute;t lớn H&agrave; Nội h&agrave;ng ng&agrave;n bạn trẻ tập trung g&acirc;y &ugrave;n tắc giao th&ocirc;ng nghi&ecirc;m trọng, v&igrave; sao?<img src=\"/storage/photos/19/post/jjwjv-1732441142797930947806.jpg\" alt=\"\" width=\"640\" height=\"480\" /></h2>\r\n<p class=\"\">Chiều 24-11, mạng x&atilde; hội xuất hiện h&igrave;nh ảnh trước khu vực&nbsp;<a class=\"link-inline-content\" title=\"Nh&agrave; h&aacute;t lớn H&agrave; Nội\" href=\"https://tuoitre.vn/nha-hat-lon-ha-noi.html\" data-rel=\"follow\">Nh&agrave; h&aacute;t lớn H&agrave; Nội</a>&nbsp;(quận Ho&agrave;n Kiếm, H&agrave; Nội) c&oacute; h&agrave;ng ng&agrave;n bạn trẻ tập trung g&acirc;y &ugrave;n tắc giao th&ocirc;ng nghi&ecirc;m trọng. Nhiều người đặt c&acirc;u hỏi tại khu vực n&agrave;y c&oacute; sự kiện g&igrave; m&agrave; lại nhiều người tập trung đến như vậy?</p>\r\n<p class=\"\">Trao đổi với&nbsp;<em>Tuổi Trẻ Online&nbsp;</em>chiều c&ugrave;ng ng&agrave;y, một l&atilde;nh đạo UBND quận Ho&agrave;n Kiếm cho biết trong tối 24-11, tại trước khu vực Nh&agrave; h&aacute;t lớn, một tập đo&agrave;n lớn của Việt Nam tổ chức sự kiện tri &acirc;n kh&aacute;ch h&agrave;ng dịp 20 năm th&agrave;nh lập.</p>\r\n<div class=\"VCSortableInPreviewMode  alignRight\" data-style=\"align-right\">\r\n<div class=\"kbwscwl-relatedbox type-2 tuoitre\">\r\n<ul class=\"kbwscwlr-list\">\r\n<li class=\"kbwscwlrl\" data-date=\"18/04/2023 14:16\" data-id=\"20230418140120731\" data-avatar=\"https://cdn.tuoitre.vn/471584752817336320/2023/4/18/img3310-16818000866882136285186-71-0-874-1284-crop-16818003762981782862724.jpg\" data-url=\"/bat-tai-xe-o-to-bieu-dien-drift-gay-nao-loan-truoc-nha-hat-lon-ha-noi-20230418140120731.htm\" data-title=\"Bắt t&agrave;i xế &ocirc; t&ocirc; \'biểu diễn\' drift g&acirc;y n&aacute;o loạn trước Nh&agrave; h&aacute;t lớn H&agrave; Nội\">\r\n<h4 class=\"kbwscwlrl-title\"><a class=\"title link-callout\" href=\"https://tuoitre.vn/bat-tai-xe-o-to-bieu-dien-drift-gay-nao-loan-truoc-nha-hat-lon-ha-noi-20230418140120731.htm\" target=\"_blank\" rel=\"noopener\">Bắt t&agrave;i xế &ocirc; t&ocirc; \'biểu diễn\' drift g&acirc;y n&aacute;o loạn trước Nh&agrave; h&aacute;t lớn H&agrave; Nội</a></h4>\r\n</li>\r\n</ul>\r\n</div>\r\n</div>\r\n<div id=\"InreadPc\"></div>\r\n<p class=\"\">Theo vị n&agrave;y, sự kiện tr&ecirc;n mời nhiều ca sĩ nổi tiếng n&ecirc;n thu h&uacute;t nhiều bạn trẻ tập trung ở đ&acirc;y từ s&aacute;ng sớm, khiến đường phố &ugrave;n tắc nghi&ecirc;m trọng.</p>\r\n<p class=\"\">\"Đường đ&ocirc;ng đ&uacute;c từ s&aacute;ng tới giờ, hiện quận đang cấm xe đi v&agrave;o khu vực tr&ecirc;n để đảm bảo an ninh trật tự. Mấy ng&agrave;n người k&eacute;o đến đ&acirc;y từ s&aacute;ng tới giờ, c&agrave;ng về chiều tối c&agrave;ng đ&ocirc;ng hơn\" - vị l&atilde;nh đạo UBND quận Ho&agrave;n Kiếm cho biết.</p>\r\n<p class=\"\">Theo t&igrave;m hiểu của&nbsp;<em>Tuổi Trẻ Online</em>, sự kiện diễn ra trước khu vực Nh&agrave; h&aacute;t lớn tối 24-11 sẽ c&oacute; sự g&oacute;p mặt của c&aacute;c ng&ocirc;i sao đ&igrave;nh đ&aacute;m h&agrave;ng đầu showbiz Việt như Sơn T&ugrave;ng M-TP, Isaac, Soobin Ho&agrave;ng Sơn, HIEUTHUHAI, H&ograve;a Minzy, Orange, Dương Domic&hellip;</p>', 'ACTIVE', '2024-11-24 11:37:31', '2024-12-04 04:59:40', 5, 19),
(6, 'Biết nghề nghiệp của chú rể, cô dâu lập tức hủy hôn', 'biet-nghe-nghiip-cua-chu-re-co-dau-lep-tuc-huy-hon', 'anh1.png', '<h2 class=\"content-detail-sapo sm-sapo-mb-0\">ẤN ĐỘ - C&ocirc; d&acirc;u hủy h&ocirc;n khi biết nghề nghiệp của ch&uacute; rể trước ng&agrave;y đ&aacute;m cưới ch&iacute;nh thức diễn ra. Mặc d&ugrave;, thu nhập của anh mỗi th&aacute;ng hơn 36 triệu đồng nhưng vẫn kh&ocirc;ng l&agrave;m c&ocirc; h&agrave;i l&ograve;ng.</h2>\r\n<h2 class=\"content-detail-sapo sm-sapo-mb-0\"><img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/storage/photos/19/post/anh1.png\" alt=\"\" width=\"640\" height=\"360\" /></h2>\r\n<p>&aacute;gasgasgasg</p>', 'ACTIVE', '2024-11-28 07:21:09', '2024-12-04 05:03:08', 5, 19),
(7, 'Nga nã tên lửa hành trình vào các thành phố khắp Ukraine', 'nga-na-ten-lua-hanh-trinh-vao-cac-thanh-pho-khap-ukraine', 'kiev-17327706053591808609966.png', '<h2 class=\"detail-sapo\" data-role=\"sapo\">Ng&agrave;y 28-11, qu&acirc;n đội Ukraine cho biết cảnh b&aacute;o kh&ocirc;ng k&iacute;ch đ&atilde; được ph&aacute;t tr&ecirc;n khắp nước n&agrave;y khi Nga ph&oacute;ng t&ecirc;n lửa v&agrave;o nhiều v&ugrave;ng.</h2>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/kiev-17327706053591808609966.png\" alt=\"\" width=\"1200\" height=\"800\" />\r\n<figcaption>\r\n<figure class=\"VCSortableInPreviewMode\">\r\n<figcaption class=\"PhotoCMS_Caption\">\r\n<p data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">Mọi người tr&uacute; ẩn b&ecirc;n trong một nh&agrave; ga t&agrave;u điện ngầm giữa l&uacute;c Nga tấn c&ocirc;ng Ukraine. Ảnh chụp tại thủ đ&ocirc; Kiev, Ukraine ng&agrave;y 28-11 - Ảnh: REUTERS</p>\r\n</figcaption>\r\n</figure>\r\n</figcaption>\r\n</figure>\r\n<p class=\"\">\"Cảnh b&aacute;o kh&ocirc;ng k&iacute;ch đ&atilde; được đưa ra tr&ecirc;n to&agrave;n l&atilde;nh thổ&nbsp;<a class=\"link-inline-content\" title=\"Ukraine\" href=\"https://tuoitre.vn/ukraine.html\" data-rel=\"follow\">Ukraine</a>&nbsp;do mối đe dọa t&ecirc;n lửa\" - kh&ocirc;ng qu&acirc;n Ukraine th&ocirc;ng b&aacute;o tr&ecirc;n ứng dụng Telegram ng&agrave;y 28-11, đồng thời cho biết họ ph&aacute;t hiện c&aacute;c t&ecirc;n lửa đang bay đến Kharkov, Odessa v&agrave; 8 v&ugrave;ng kh&aacute;c, theo H&atilde;ng tin AFP.</p>\r\n<p class=\"\">\"Kharkov, h&atilde;y v&agrave;o địa điểm tr&uacute; ẩn!\" - kh&ocirc;ng qu&acirc;n Ukraine cảnh b&aacute;o.</p>\r\n<p class=\"\">H&atilde;ng Reuters cũng đưa tin Nga tấn c&ocirc;ng c&aacute;c th&agrave;nh phố tr&ecirc;n khắp Ukraine bằng t&ecirc;n lửa h&agrave;nh tr&igrave;nh.</p>\r\n<p class=\"\">B&aacute;o<em>&nbsp;Zerkalo Tyzhnya</em>&nbsp;v&agrave; Đ&agrave;i Suspilne của Ukraine tường thuật người ta nghe thấy tiếng nổ ở c&aacute;c th&agrave;nh phố Odessa, Kropyvnytskyi, Kharkov, Rivne v&agrave; Lutsk của Ukraine v&agrave;o s&aacute;ng 28-11, trong bối cảnh c&oacute; tin Nga tấn c&ocirc;ng bằng&nbsp;<a class=\"file-download-link\" title=\"t&ecirc;n lửa\" href=\"https://tuoitre.vn/thu-do-ukraine-rung-chuyen-vi-nga-tan-cong-ten-lua-20241113142957917.htm\" target=\"_blank\" rel=\"noopener\">t&ecirc;n lửa</a>&nbsp;h&agrave;nh tr&igrave;nh.</p>\r\n<p class=\"\">\"Gần s&aacute;ng, Nga đ&atilde; triển khai 7 m&aacute;y bay n&eacute;m bom chiến lược Tu-95MS l&ecirc;n kh&ocirc;ng trung. Sau đ&oacute; v&agrave;o l&uacute;c 5h03 s&aacute;ng (theo giờ Kiev), họ đ&atilde; ph&oacute;ng t&ecirc;n lửa h&agrave;nh tr&igrave;nh Kalibr từ biển Đen\" - H&atilde;ng RBC, Ukraine đăng th&ocirc;ng tin.</p>\r\n<p class=\"\">Tr&ecirc;n Facebook, Bộ trưởng Năng lượng Ukraine Herman Halushchenko cho biết cơ sở hạ tầng năng lượng nước n&agrave;y \"một lần nữa trở th&agrave;nh mục ti&ecirc;u trong cuộc tấn c&ocirc;ng quy m&ocirc; lớn của đối phương\".</p>\r\n<p class=\"\">C&ograve;n thị trưởng Kharkov Ihor Terekhov viết tr&ecirc;n Telegram: \"Đối phương tiếp tục tấn c&ocirc;ng Kharkov bằng t&ecirc;n lửa\".</p>\r\n<p class=\"\">Thống đốc v&ugrave;ng Odessa Oleh Kiper đ&atilde; k&ecirc;u gọi người d&acirc;n ở tại nơi tr&uacute; ẩn.</p>\r\n<p class=\"\">Đợt tấn c&ocirc;ng t&ecirc;n lửa mới nhất của Nga diễn ra một ng&agrave;y sau khi Tổng thống đắc cử Mỹ Donald Trump chọn tướng về hưu Keith Kellogg l&agrave;m \"đặc ph&aacute;i vi&ecirc;n về Ukraine v&agrave; Nga\" trong ch&iacute;nh quyền sắp tới, với nhiệm vụ chấm dứt cuộc xung đột n&agrave;y.</p>\r\n<p class=\"\">Xung đột Nga - Ukraine đang tiếp tục tăng nhiệt. H&ocirc;m 21-11, Nga ph&oacute;ng t&ecirc;n lửa đạn đạo si&ecirc;u vượt &acirc;m tầm trung mới c&oacute; t&ecirc;n&nbsp;<a class=\"file-download-link\" title=\"Oreshnik\" href=\"https://tuoitre.vn/can-canh-manh-vo-ten-lua-sieu-vuot-am-oreshnik-20241125063417059.htm\" target=\"_blank\" rel=\"noopener\">Oreshnik</a>&nbsp;v&agrave;o một cơ sở quốc ph&ograve;ng của Ukraine tại th&agrave;nh phố Dnipro.</p>\r\n<p class=\"\">Tổng thống Nga Vladimir Putin giải th&iacute;ch việc sử dụng t&ecirc;n lửa n&agrave;y l&agrave; để đ&aacute;p trả c&aacute;c cuộc tấn c&ocirc;ng của Ukraine v&agrave;o l&atilde;nh thổ Nga bằng vũ kh&iacute; tầm xa do phương T&acirc;y cung cấp như t&ecirc;n lửa ATACMS của Mỹ v&agrave; t&ecirc;n lửa Storm Shadow của Anh.</p>', 'ACTIVE', '2024-11-28 07:42:56', '2024-11-28 07:43:52', 5, 19),
(8, 'Việt Nam trước cơ hội đến từ Chiến lược Kinh tế Đông Nam Á của Úc', 'viet-nam-truoc-co-hoi-den-tu-chien-luoc-kinh-te-dong-nam-a-cua-uc', 'hinh-2-17329473228421505517634.png', '<h2 class=\"detail-sapo has-audio\" data-role=\"sapo\">Sau một năm &Uacute;c thực thi Chiến lược Kinh tế Đ&ocirc;ng Nam &Aacute; đến 2040, b&agrave; Louise Adams, đại diện ch&iacute;nh phủ &Uacute;c x&uacute;c tiến đầu tư tại Việt Nam, đ&atilde; trao đổi với b&aacute;o ch&iacute; TP.HCM về những g&igrave; đạt được trong năm qua.</h2>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/hinh-2-17329473228421505517634.png\" alt=\"\" width=\"640\" height=\"480\" />\r\n<figcaption>\r\n<p data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">Đo&agrave;n doanh nghiệp &Uacute;c t&igrave;m hiểu quy tr&igrave;nh chế biến gạo ở nh&agrave; m&aacute;y SunRice tại tỉnh Đồng Th&aacute;p</p>\r\n<div class=\"PhotoCMS_Author\">\r\n<p class=\"\" data-placeholder=\"Nhập t&aacute;c giả\">ảnh: thụy mi&ecirc;n</p>\r\n</div>\r\n</figcaption>\r\n</figure>\r\n<p>\"Trong một năm qua, ch&uacute;ng t&ocirc;i đang x&acirc;y dựng động lực cho&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Chiến lược Kinh tế Đ&ocirc;ng Nam &Aacute;\" href=\"https://thanhnien.vn/chien-luoc-kinh-te-dong-nam-a.html\" target=\"_blank\" rel=\"noopener\">Chiến lược Kinh tế Đ&ocirc;ng Nam &Aacute;</a>&nbsp;đến 2040\", b&agrave; Adams cho biết, th&ecirc;m rằng b&agrave; v&ocirc; c&ugrave;ng lạc quan trước những kết quả đang chờ gặt h&aacute;i ở&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Việt Nam\" href=\"https://thanhnien.vn/viet-nam.html\" target=\"_blank\" rel=\"noopener\">Việt Nam</a>&nbsp;v&agrave; khu vực.</p>\r\n<h2><strong>Tăng cường x&uacute;c tiến cơ hội hợp t&aacute;c đầu tư</strong></h2>\r\n<p>B&agrave; Louise Adams, Doanh nh&acirc;n Ti&ecirc;n phong &Uacute;c tại Việt Nam ki&ecirc;m th&agrave;nh vi&ecirc;n Ban Cố vấn Trung t&acirc;m ASEAN-&Uacute;c, cho biết v&agrave;o th&aacute;ng 6 đ&atilde; đồng dẫn đo&agrave;n&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\" doanh nghiệp &Uacute;c\" href=\"https://thanhnien.vn/doanh-nghiep-uc.html\" target=\"_blank\" rel=\"noopener\">doanh nghiệp &Uacute;c</a>&nbsp;đến thăm v&agrave; x&uacute;c tiến cơ hội hợp t&aacute;c, đầu tư tại Việt Nam.</p>\r\n<p>Theo b&agrave;, chuyến c&ocirc;ng t&aacute;c đặc biệt tập trung v&agrave;o lĩnh vực c&ocirc;ng nghệ kỹ thuật số, với sự tham gia của c&aacute;c h&atilde;ng c&ocirc;ng nghệ t&agrave;i ch&iacute;nh (Fintech), c&ocirc;ng nghệ gi&aacute;o dục, c&ocirc;ng nghệ n&ocirc;ng nghiệp v&agrave; một số tổ chức an ninh mạng.</p>\r\n<p>\"Nhờ v&agrave;o cam kết của ch&iacute;nh phủ &Uacute;c đối với chiến lược x&uacute;c tiến thương mại với Việt Nam, ch&uacute;ng t&ocirc;i (ph&iacute;a doanh nghiệp &Uacute;c) ph&aacute;t hiện được mối quan t&acirc;m tăng mạnh từ c&aacute;c c&ocirc;ng ty/tổ chức trong những lĩnh vực tr&ecirc;n ở Việt Nam\", theo b&agrave; Adams.</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, b&agrave; cho hay chuyến đi cũng mở rộng cơ hội hợp t&aacute;c hơn nữa trong c&aacute;c lĩnh vực ưu ti&ecirc;n như chuyển đổi năng lượng, kinh tế xanh, gi&aacute;o dục, n&ocirc;ng nghiệp. \"Cơ hội đang mở rộng hơn trong mảng n&ocirc;ng nghiệp, kh&ocirc;ng chỉ dừng lại ở n&ocirc;ng nghiệp kỹ thuật số m&agrave; c&ograve;n l&agrave; m&ocirc;i trường n&ocirc;ng nghiệp rộng hơn nữa, v&agrave; kế đến l&agrave; chuyển đổi số\", nữ doanh nh&acirc;n ti&ecirc;n phong của &Uacute;c cho biết.</p>\r\n<p>Theo b&agrave;, chuyến c&ocirc;ng t&aacute;c cho thấy tầm quan trọng của việc cần phải tăng cường tiếp x&uacute;c giữa c&aacute;c đo&agrave;n doanh nghiệp v&agrave; d&agrave;nh nhiều thời gian hơn để x&acirc;y dựng những mối quan hệ song phương n&agrave;y.</p>\r\n<p>Trong l&uacute;c c&ograve;n nhiều việc phải l&agrave;m, điều b&agrave; đặc biệt vui mừng ở Việt Nam l&agrave; ph&iacute;a doanh nghiệp nhận được cam kết hỗ trợ to&agrave;n phần từ cả ch&iacute;nh phủ &Uacute;c lẫn ch&iacute;nh phủ Việt Nam trong việc ủng hộ doanh nghiệp nhanh ch&oacute;ng vượt qua những vấn đề c&oacute; thể g&acirc;y trở ngại, hoặc những kh&acirc;u c&aacute;c c&ocirc;ng ty gặp trục trặc khi hoạt động trong c&aacute;c m&ocirc;i trường ph&aacute;p l&yacute; kh&aacute;c biệt vốn tồn tại giữa hai nước.</p>\r\n<p>\"Ch&uacute;ng t&ocirc;i c&oacute; được sự cam kết của ch&iacute;nh phủ v&agrave; ng&agrave;nh, cũng như từ c&aacute;c cơ quan được x&acirc;y dựng nhằm mục đ&iacute;ch hỗ trợ c&aacute;c doanh nghiệp vượt qua những sự kh&aacute;c biệt với tốc độ c&oacute; lẽ nhanh hơn so với trước đ&acirc;y. V&igrave; thế t&ocirc;i v&ocirc; c&ugrave;ng lạc quan về chiến lược n&agrave;y, v&agrave; t&ocirc;i v&ocirc; c&ugrave;ng lạc quan về quan hệ giữa &Uacute;c với c&aacute;c nước ASEAN, đặc biệt l&agrave; trong mối quan hệ với Việt Nam\", theo th&agrave;nh vi&ecirc;n Ban Cố vấn Trung t&acirc;m ASEAN-&Uacute;c.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/hinh-1-17329473228022082923991.png\" alt=\"\" width=\"640\" height=\"480\" />\r\n<figcaption>N&ocirc;ng d&acirc;n tham gia dự &aacute;n \'chuyển đổi Chuỗi gi&aacute; trị L&uacute;a gạo ứng ph&oacute; với <br />Biến đổi Kh&iacute; hậu v&agrave; Ph&aacute;t triển Bền vững tại ĐBSCL\' ở tỉnh Đồng Th&aacute;p</figcaption>\r\n</figure>\r\n<h2>Kh&aacute;m ph&aacute; yếu tố mới trong c&aacute;c lĩnh vực ưu ti&ecirc;n</h2>\r\n<p>B&agrave; Adams cũng n&oacute;i về những yếu tố mới trong c&aacute;c lĩnh vực ưu ti&ecirc;n hợp t&aacute;c truyền thống như n&ocirc;ng nghiệp v&agrave; thực phẩm, t&agrave;i nguy&ecirc;n, chuyển đổi năng lượng xanh, gi&aacute;o dục v&agrave; kỹ năng.</p>\r\n<p>Chẳng hạn, b&ecirc;n cạnh mối li&ecirc;n kết mạnh mẽ trong lĩnh vực gi&aacute;o dục đại học, phản &aacute;nh qua số lượng du học sinh Việt Nam đến &Uacute;c, b&agrave; đề cập xu hướng c&aacute;c t&ecirc;n tuổi gi&aacute;o dục của &Uacute;c đang đến khu vực v&agrave; c&oacute; &yacute; định đầu tư x&acirc;y trường tại chỗ.</p>\r\n<p>Ngo&agrave;i ra, ph&iacute;a ch&iacute;nh phủ &Uacute;c đồng thời hỗ trợ ph&iacute;a Việt Nam x&acirc;y dựng khu&ocirc;n khổ gi&aacute;o dục ri&ecirc;ng, khu&ocirc;n khổ gi&aacute;o dục đại học v&agrave; tăng cường năng lực gi&aacute;o dục đại học tại địa phương.</p>\r\n<p>C&ograve;n về n&ocirc;ng nghiệp, b&ecirc;n cạnh c&aacute;c hợp t&aacute;c truyền thống trong lĩnh vực n&agrave;y, &Uacute;c v&agrave; Việt Nam đồng thời cũng đối mặt những th&aacute;ch thức mới, c&oacute; thể kể đến ảnh hưởng của biến đổi kh&iacute; hậu v&agrave; vấn đề an ninh lương thực.</p>\r\n<p>\"T&ocirc;i cho rằng cả Việt Nam v&agrave; &Uacute;c đối mặt những th&aacute;ch thức giống nhau xoay quanh vấn đề an ninh lương thực ở tầm quốc gia v&igrave; ảnh hưởng của biến đổi kh&iacute; hậu\", theo b&agrave; Adams, cụ thể l&agrave; ở khu vực Đồng bằng S&ocirc;ng Cửu Long. Hai b&ecirc;n đang triển khai một số cuộc nghi&ecirc;n cứu để hướng đến tăng cường năng lực chống chịu của c&acirc;y l&uacute;a trong điều kiện m&ocirc;i trường thay đổi.</p>\r\n<p>V&agrave; t&agrave;i nguy&ecirc;n l&agrave; đầu v&agrave;o quan trọng cho c&aacute;c s&aacute;ng kiến trong lĩnh vực năng lượng sạch. C&aacute;c nền kinh tế cần t&agrave;i nguy&ecirc;n để đạt được những tham vọng trong lĩnh vực n&agrave;y. \"&Uacute;c v&agrave; c&aacute;c th&agrave;nh vi&ecirc;n ASEAN đang đứng trước nhiều cơ hội hợp t&aacute;c li&ecirc;n quan c&aacute;c loại kho&aacute;ng sản then chốt\", b&agrave; cho biết.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/hinh-4-1732947322914432294380.png\" alt=\"\" width=\"640\" height=\"820\" />\r\n<figcaption>CB&agrave; Louise Adams, Doanh nh&acirc;n Ti&ecirc;n phong &Uacute;c tại Việt Nam ki&ecirc;m th&agrave;nh vi&ecirc;n <br />Ban Cố vấn Trung t&acirc;m ASEAN-&Uacute;caption</figcaption>\r\n</figure>\r\n<h2>B&iacute; quyết cho doanh nghiệp &Uacute;c muốn đầu tư v&agrave;o Việt Nam</h2>\r\n<p>Ngo&agrave;i hai vai tr&ograve; Doanh nh&acirc;n Ti&ecirc;n phong &Uacute;c tại Việt Nam v&agrave; th&agrave;nh vi&ecirc;n Ban Cố vấn Trung t&acirc;m ASEAN-&Uacute;c, b&agrave; Adams trước hết l&agrave; nữ doanh nh&acirc;n với sự nghiệp th&agrave;nh c&ocirc;ng suốt hơn 25 năm qua. B&agrave; l&agrave; gi&aacute;m đốc điều h&agrave;nh của c&ocirc;ng ty tư vấn, kỹ thuật v&agrave; thiết kế Aurecon, c&oacute; mặt ở Việt Nam từ năm 1991.</p>\r\n<p>Chia sẻ về kinh nghiệm th&agrave;nh c&ocirc;ng ở Việt Nam, b&agrave; cho hay một trong số đ&oacute; ch&iacute;nh l&agrave; \"x&acirc;y dựng c&aacute;c mối quan hệ\". \"Cần phải đến Việt Nam, đầu tư thời gian gặp gỡ mọi người v&agrave; x&acirc;y dựng l&ograve;ng tin\", theo b&agrave; Adams, th&ecirc;m rằng nhiều mối quan hệ l&agrave;m ăn ở Việt Nam v&agrave; Đ&ocirc;ng Nam &Aacute; được x&acirc;y dựng tr&ecirc;n nền tảng của sự tin tưởng lẫn nhau.</p>\r\n<p>B&iacute; quyết kế tiếp l&agrave; c&aacute;c doanh nghiệp &Uacute;c n&ecirc;n suy nghĩ hết sức thận trọng về c&aacute;ch họ nh&igrave;n nhận khu vực từ g&oacute;c độ lợi tức đầu tư. \"&Yacute; tưởng cho rằng bạn c&oacute; thể đưa c&ocirc;ng ty &Uacute;c đến Đ&ocirc;ng Nam &Aacute; v&agrave; kiếm tiền nhanh ch&oacute;ng l&agrave; suy nghĩ phi thực tế. Vậy th&igrave; tầm nh&igrave;n trung v&agrave; d&agrave;i hạn của c&ocirc;ng ty bạn l&agrave; g&igrave;? V&agrave; bạn nh&igrave;n nhận khoản đầu tư tại khu vực theo kh&iacute;a cạnh trung v&agrave; d&agrave;i hạn như thế n&agrave;o?\", b&agrave; nhấn mạnh.</p>\r\n<p>Lời khuy&ecirc;n cuối c&ugrave;ng ch&iacute;nh l&agrave; c&aacute;c doanh nh&acirc;n &Uacute;c v&agrave; VIệt Nam c&oacute; thể nắm bắt cơ hội t&igrave;m hiểu m&ocirc;i trường đầu tư ở nước bạn th&ocirc;ng qua những cơ chế đang được ch&iacute;nh phủ hai nước triển khai. Việc t&igrave;m hiểu dựa v&agrave;o những cơ chế n&agrave;y sẽ gi&uacute;p ph&iacute;a doanh nghiệp biết được c&aacute;c bước thực hiện, những y&ecirc;u cầu/ti&ecirc;u chuẩn cần đ&aacute;p ứng, cần loại giấy ph&eacute;p g&igrave;...</p>\r\n<p>B&agrave; Adams chia sẻ bản th&acirc;n cố gắng lui tới Đ&ocirc;ng Nam &Aacute; khoảng 3-4 lần/năm, đầu tư thời gian v&agrave;o khu vực v&agrave; gặp gỡ c&aacute;c đối t&aacute;c để siết chặt quan hệ kinh doanh.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/hinh-3-1732947322882110613312.png\" alt=\"\" width=\"640\" height=\"496\" />\r\n<figcaption>M&ocirc; h&igrave;nh nu&ocirc;i t&ocirc;m chi ph&iacute; thấp, hiệu quả cao của doanh nghiệp &Uacute;c ở ĐBSCL</figcaption>\r\n</figure>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<h2 class=\"detail-sapo has-audio\" data-role=\"sapo\">&nbsp;</h2>', 'ACTIVE', '2024-11-30 09:08:57', '2024-11-30 09:08:57', 5, 19),
(9, 'Mỹ duyệt bán gói thiết bị quân sự 385 triệu USD cho Đài Loan', 'my-duyit-ban-goi-thiet-bo-quan-sy-385-triiu-usd-cho-ai-loan', 'afp2024052434tr6fdv2highrestopsh.png', '<h2 class=\"detail-sapo has-audio\" data-role=\"sapo\">Mỹ đ&atilde; duyệt b&aacute;n g&oacute;i phụ t&ugrave;ng chiến đấu cơ F-16 v&agrave; c&aacute;c trang thiết bị kh&aacute;c trị gi&aacute; ước t&iacute;nh 385 triệu USD cho Đ&agrave;i Loan ngay trước khi l&atilde;nh đạo h&ograve;n đảo dừng ch&acirc;n tại Mỹ.</h2>\r\n<div class=\"detail-cmain\">\r\n<div class=\"detail-content afcbc-body\" data-role=\"content\" data-io-article-url=\"https://thanhnien.vn/my-duyet-ban-goi-thiet-bi-quan-su-385-trieu-usd-cho-dai-loan-185241130124530022.htm\">\r\n<div data-check-position=\"body_start\">&nbsp;</div>\r\n<p><a class=\"VCCAutoLink link-inline-content\" title=\"Bộ Quốc ph&ograve;ng\" href=\"https://thanhnien.vn/thoi-su/quoc-phong.htm\" target=\"_blank\" rel=\"noopener\" data-id-autolink=\"11505\">Bộ Quốc ph&ograve;ng</a>&nbsp;Mỹ ng&agrave;y 29.11 th&ocirc;ng b&aacute;o rằng Bộ Ngoại giao nước n&agrave;y đ&atilde; ph&ecirc; duyệt việc b&aacute;n phụ t&ugrave;ng chiến đấu cơ F-16 v&agrave; c&aacute;c trang thiết bị qu&acirc;n sự cho&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Đ&agrave;i Loan\" href=\"https://thanhnien.vn/dai-loan.html\" target=\"_blank\" rel=\"noopener\">Đ&agrave;i Loan</a>. Thỏa thuận được ước t&iacute;nh trị gi&aacute; 385 triệu USD, theo Reuters.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/afp2024052434tr6fdv2highrestopsh.png\" alt=\"\" width=\"640\" height=\"427\" />\r\n<figcaption>Chiến đấu cơ F-16 tập trận tại Đ&agrave;i Loan hồi th&aacute;ng 5</figcaption>\r\n</figure>\r\n<p>Cơ quan Hợp t&aacute;c&nbsp;<a class=\"VCCAutoLink link-inline-content\" title=\"quốc ph&ograve;ng an ninh\" href=\"https://thanhnien.vn/thoi-su/quoc-phong.htm\" target=\"_blank\" rel=\"noopener\" data-id-autolink=\"11504\">quốc ph&ograve;ng an ninh</a>&nbsp;(DSCA) của Lầu Năm G&oacute;c cho biết thỏa thuận gồm c&aacute;c phụ t&ugrave;ng v&agrave; hỗ trợ chiến đấu cơ&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"F-16 Đ&agrave;i Loan theo d&otilde;i oanh tạc cơ Trung Quốc diễn tập\" href=\"https://thanhnien.vn/f-16-dai-loan-theo-doi-oanh-tac-co-trung-quoc-dien-tap-185760016.htm\" target=\"_blank\" rel=\"noopener\">F-16</a>, c&aacute;c radar điện tử chủ động v&agrave; trang thiết bị li&ecirc;n quan trị gi&aacute; 320 triệu USD.</p>\r\n<p>Bộ Ngoại giao Mỹ c&ograve;n đồng &yacute; b&aacute;n c&aacute;c thiết bị li&ecirc;n lạc di động n&acirc;ng cấp v&agrave; hỗ trợ trị gi&aacute; ước t&iacute;nh 65 triệu USD. Nh&agrave; thầu ch&iacute;nh của phần n&agrave;y l&agrave; General Dynamics.</p>\r\n<p>Cơ quan Ph&ograve;ng vệ Đ&agrave;i Loan kỳ vọng c&aacute;c hợp đồng sẽ c&oacute; hiệu lực trong v&ograve;ng một th&aacute;ng v&agrave; c&aacute;c trang thiết bị tr&ecirc;n sẽ gi&uacute;p duy tr&igrave; sự sẵn s&agrave;ng cho phi đội F-16, x&acirc;y dựng một lực lượng ph&ograve;ng thủ đ&aacute;ng tin cậy.</p>\r\n<p>\"<a class=\"seo-suggest-link link-inline-content\" title=\"Đ&agrave;i Loan muốn chứng tỏ với &ocirc;ng Trump về nỗ lực củng cố ph&ograve;ng vệ?\" href=\"https://thanhnien.vn/dai-loan-muon-chung-to-voi-ong-trump-ve-no-luc-cung-co-phong-ve-1852411141436131.htm\" target=\"_blank\" rel=\"noopener\">Đ&agrave;i Loan</a>&nbsp;v&agrave; Mỹ sẽ tiếp tục tăng cường mối quan hệ đối t&aacute;c an ninh v&agrave; l&agrave;m việc c&ugrave;ng nhau để duy tr&igrave;&nbsp;<a class=\"VCCAutoLink link-inline-content\" title=\"h&ograve;a b&igrave;nh\" href=\"https://thanhnien.vn/hoa-binh.html\" target=\"_blank\" rel=\"noopener\" data-id-autolink=\"8098\">h&ograve;a b&igrave;nh</a>&nbsp;v&agrave; ổn định tại eo biển Đ&agrave;i Loan v&agrave; khu vực Ấn Độ Dương-Th&aacute;i&nbsp;<a class=\"VCCAutoLink link-inline-content\" title=\"B&igrave;nh Dương\" href=\"https://thanhnien.vn/binh-duong-tags486331.html\" target=\"_blank\" rel=\"noopener\" data-id-autolink=\"8057\">B&igrave;nh Dương</a>\", cơ quan n&oacute;i tr&ecirc;n tuy&ecirc;n bố.</p>\r\n<p>Th&aacute;ng trước, Mỹ c&ocirc;ng bố g&oacute;i hợp đồng b&aacute;n vũ kh&iacute; trị gi&aacute; 2 tỉ USD cho Đ&agrave;i Loan, gồm c&oacute; Hệ thống t&ecirc;n lửa ph&ograve;ng kh&ocirc;ng ti&ecirc;n tiến quốc gia (NASAMS) đ&atilde; được thử nghiệm tại chiến trường Ukraine.</p>\r\n<p>Hợp đồng mới nhất được ph&ecirc; duyệt khi l&atilde;nh đạo Đ&agrave;i Loan&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Lại Thanh Đức\" href=\"https://thanhnien.vn/lai-thanh-duc.html\" target=\"_blank\" rel=\"noopener\">Lại Thanh Đức</a>&nbsp;bắt đầu chuyến c&ocirc;ng du 3 nước&nbsp;<a class=\"VCCAutoLink link-inline-content\" title=\"Th&aacute;i B&igrave;nh\" href=\"https://thanhnien.vn/thai-binh-tags506474.html\" target=\"_blank\" rel=\"noopener\" data-id-autolink=\"8156\">Th&aacute;i B&igrave;nh</a>&nbsp;Dương gồm Quần đảo Marshall, Tuvalu v&agrave; Palau trong ng&agrave;y 30.11. &Ocirc;ng Lại sẽ dừng ch&acirc;n tại Hawaii v&agrave; l&atilde;nh thổ Guam của Mỹ tr&ecirc;n h&agrave;nh tr&igrave;nh n&agrave;y.</p>\r\n<p>Trung Quốc, vốn coi Đ&agrave;i Loan l&agrave; một phần l&atilde;nh thổ kh&ocirc;ng thể t&aacute;ch rời v&agrave; kh&ocirc;ng loại trừ khả năng d&ugrave;ng vũ lực để t&aacute;i thống nhất, ng&agrave;y 29.11 k&ecirc;u gọi Mỹ hết sức thận trọng trong quan hệ với Đ&agrave;i Loan.</p>\r\n<p>Trong khi đ&oacute;, Bộ Ngoại giao Mỹ n&oacute;i kh&ocirc;ng c&oacute; l&yacute; do g&igrave; cho việc sử dụng&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Đ&agrave;i Loan tổ chức tập trận ph&ograve;ng kh&ocirc;ng trước l&uacute;c l&atilde;nh đạo dự qu&aacute; cảnh tại Mỹ\" href=\"https://thanhnien.vn/dai-loan-to-chuc-tap-tran-phong-khong-truoc-luc-lanh-dao-du-qua-canh-tai-my-185241128110218621.htm\" target=\"_blank\" rel=\"noopener\">chuyến qu&aacute; cảnh</a>&nbsp;ri&ecirc;ng tư, thường lệ v&agrave; kh&ocirc;ng ch&iacute;nh thức của &ocirc;ng Lại l&agrave;m c&aacute;i cớ để khi&ecirc;u kh&iacute;ch.</p>\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>', 'ACTIVE', '2024-11-30 09:10:38', '2024-12-04 05:00:53', 5, 19),
(10, 'Vụ trả giá 30 tỉ đồng/m2 đất: Kế hoạch phá đấu giá của nhóm nghi phạm', 'vu-tra-gia-30-ti-dongm2-dat-ke-hoach-pha-dau-gia-cua-nhom-nghi-pham', 'img7088-173323101218691307826.png', '<h2 class=\"detail-sapo has-audio\" data-role=\"sapo\">Thấy gi&aacute; đấu c&aacute;c l&ocirc; đất vượt mức tối đa m&agrave; nh&oacute;m đ&atilde; b&agrave;n bạc, dự kiến n&ecirc;n Phạm Ngọc Tuấn c&ugrave;ng đồng bọn trả gi&aacute; cao, thậm ch&iacute; c&oacute; l&ocirc; trả tới 30 tỉ đồng/m2 để ph&aacute; phi&ecirc;n đấu gi&aacute;.</h2>\r\n<div class=\"detail-cmain\">\r\n<div class=\"detail-content afcbc-body\" data-role=\"content\" data-io-article-url=\"https://thanhnien.vn/vu-tra-gia-30-ti-dong-m2-dat-ke-hoach-pha-dau-gia-cua-nhom-nghi-pham-185241203200656428.htm\">\r\n<div data-check-position=\"body_start\">&nbsp;</div>\r\n<p data-id-seo=\"paragraphs-0\">C&ocirc;ng an H&agrave; Nội vừa ph&aacute;t th&ocirc;ng tin ch&iacute;nh thức li&ecirc;n quan vụ trả gi&aacute; hơn 30 tỉ đồng/m<sup>2</sup>&nbsp;để ph&aacute; phi&ecirc;n&nbsp;<a class=\"link-inline-content\" title=\"đấu gi&aacute; đất\" href=\"https://thanhnien.vn/dau-gia-dat-30-ti-dong-met-vuong-de-pha-xu-ly-duoc-khong-185241130230117695.htm\" data-rel=\"follow\">đấu gi&aacute; đất</a>&nbsp;ở H.S&oacute;c Sơn (H&agrave; Nội).</p>\r\n<p data-id-seo=\"paragraphs-1\">Ng&agrave;y 3.12, Ph&ograve;ng Cảnh s&aacute;t điều tra tội phạm về&nbsp;<a class=\"link-inline-content\" title=\"kinh tế\" href=\"https://thanhnien.vn/quan-tri-ben-vung-bwf-ventures-va-su-menh-phat-trien-kinh-te-xanh-185241203153454079.htm\" data-rel=\"follow\">kinh tế</a>, tham nhũng, bu&ocirc;n lậu (PC03) C&ocirc;ng an H&agrave; Nội đ&atilde; ra quyết định tạm giữ h&igrave;nh sự đối với Phạm Ngọc Tuấn (33 tuổi, tr&uacute; TT.Đ&ocirc;ng Anh, H.Đ&ocirc;ng Anh, H&agrave; Nội), Ng&ocirc; Văn Dương (30 tuổi, tr&uacute; x&atilde; Xu&acirc;n Nộn, H.Đ&ocirc;ng Anh), Nguyễn Đức Th&agrave;nh (32 tuổi, tr&uacute; P.V&acirc;n Dương, TP.Bắc Ninh, Bắc Ninh), Nguyễn Thế Trung (30 tuổi, tr&uacute; x&atilde; Xu&acirc;n Nộn) v&agrave; Nguyễn Thế Qu&acirc;n (30 tuổi, tr&uacute; x&atilde; Xu&acirc;n Nộn), để điều tra h&agrave;nh vi vi phạm quy định về hoạt động b&aacute;n đấu gi&aacute; t&agrave;i sản, quy định tại khoản 2 điều 218 bộ luật H&igrave;nh sự năm 2015.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/img7088-173323101218691307826.png\" alt=\"\" width=\"640\" height=\"474\" />\r\n<figcaption>\r\n<p class=\"NLPlaceholderShow\" data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">C&aacute;c nghi phạm trong vụ việc</p>\r\n</figcaption>\r\n</figure>\r\n<p data-id-seo=\"paragraphs-4\">Theo kết quả điều tra ban đầu, th&aacute;ng 11, Tuấn nắm được th&ocirc;ng tin về cuộc đấu gi&aacute; quyền sử dụng đất tại dự &aacute;n x&acirc;y dựng hạ tầng kỹ thuật chỉnh trang, ph&aacute;t triển khu d&acirc;n cư n&ocirc;ng th&ocirc;n tại th&ocirc;n Đ&ocirc;ng Lai (x&atilde; Quang Tiến, H.S&oacute;c Sơn) n&ecirc;n nhờ Dương mua hồ sơ đấu gi&aacute; do C&ocirc;ng ty Thanh Xu&acirc;n (đơn vị tổ chức đấu gi&aacute;) ph&aacute;t h&agrave;nh.</p>\r\n<p data-id-seo=\"paragraphs-5\">Để chắc chắn tr&uacute;ng đấu gi&aacute; được c&aacute;c l&ocirc; đất như &yacute;, Tuấn thỏa thuận, b&agrave;n bạc với Nguyễn Thị Quỳnh Li&ecirc;n (43 tuổi, tr&uacute; x&atilde; Nguy&ecirc;n Kh&ecirc;, H.Đ&ocirc;ng Anh) v&agrave; 4 nghi phạm kể tr&ecirc;n về việc c&ugrave;ng tham gia đấu gi&aacute;, sau đ&oacute; thống nhất sẽ n&acirc;ng gi&aacute; tại buổi đấu gi&aacute;.</p>\r\n<p data-id-seo=\"paragraphs-6\">Cụ thể, Tuấn đưa ra bảng gi&aacute; tham khảo đối với từng l&ocirc; đất do Tuấn t&iacute;nh to&aacute;n, chuẩn bị từ trước. Theo bảng t&iacute;nh n&agrave;y, c&aacute;c l&ocirc; đất c&oacute; gi&aacute; trị dao động khoảng từ 20 - 32 triệu đồng/m<sup>2</sup>, ước t&iacute;nh từ 1,7 - 3,9 tỉ đồng/l&ocirc; đất.</p>\r\n<p data-id-seo=\"paragraphs-7\">Nh&oacute;m của Tuấn x&aacute;c định đ&acirc;y l&agrave; gi&aacute; cao nhất c&oacute; thể mua của từng l&ocirc; đất n&ecirc;n nếu ở v&ograve;ng thứ 4, người trả gi&aacute; cao nhất vẫn ở dưới mức gi&aacute; Tuấn đ&atilde; ghi trong bảng t&iacute;nh th&igrave; Dương, Li&ecirc;n, Th&agrave;nh, Qu&acirc;n v&agrave; Trung sẽ tham gia tiếp v&ograve;ng thứ 5 v&agrave; v&ograve;ng thứ 6 nhưng kh&ocirc;ng trả gi&aacute; vượt qu&aacute; mức gi&aacute; do Tuấn đ&atilde; thẩm định.</p>\r\n<p data-id-seo=\"paragraphs-8\">Nếu v&ograve;ng thứ 4 c&oacute; người trả gi&aacute; vượt mức gi&aacute; tối đa do Tuấn đưa ra th&igrave; c&aacute;c nghi phạm sẽ đưa ra một mức gi&aacute; cao bất thường tại v&ograve;ng thứ 5, nhưng bỏ đấu gi&aacute; tại v&ograve;ng cuối c&ugrave;ng (v&ograve;ng thứ 6). V&igrave; vậy, việc đấu gi&aacute; sẽ buộc phải dừng để tổ chức đấu gi&aacute; lại v&agrave;o lần sau, do kh&ocirc;ng đấu gi&aacute; đủ 6 v&ograve;ng theo quy chế. Khi đ&oacute;, nh&oacute;m của Tuấn sẽ kh&ocirc;ng mất tiền đặt cọc v&agrave; sẽ c&oacute; cơ hội tiếp tục tham gia đấu gi&aacute; để mua được l&ocirc; đất như mong muốn.</p>\r\n<p data-id-seo=\"paragraphs-9\">Để thực hiện &yacute; đồ, cả nh&oacute;m đ&atilde; chuyển khoản tiền cho Dương v&agrave; Tuấn, sau đ&oacute; Tuấn chuyển khoản tổng số tiền 3,616 tỉ đồng cho C&ocirc;ng ty Thanh Xu&acirc;n để đặt cọc&nbsp;<a class=\"link-inline-content\" title=\"đấu gi&aacute;\" href=\"https://thanhnien.vn/288-trieu-dong-tu-chuong-trinh-dau-gia-gay-quy-tu-thien-cham-lo-cho-cong-dong-185241202144405768.htm\" data-rel=\"follow\">đấu gi&aacute;</a>&nbsp;mua đất.</p>\r\n<p data-id-seo=\"paragraphs-10\">Tại phi&ecirc;n đấu gi&aacute; ng&agrave;y 29.11, ban đầu nh&oacute;m của Tuấn đấu gi&aacute; theo tr&igrave;nh tự gi&aacute; đạt mức c&oacute; thể mua được. Tuy nhi&ecirc;n, khi ph&aacute;t hiện gi&aacute; đấu của 36/58 l&ocirc; đất vượt mức tối đa m&agrave; nh&oacute;m đ&atilde; b&agrave;n bạc từ trước, nh&oacute;m n&agrave;y đ&atilde; đưa ra mức gi&aacute; rất cao, vượt xa gi&aacute; khởi điểm tại v&ograve;ng đấu thứ 5. Thậm ch&iacute;, Tuấn c&ograve;n đưa ra mức gi&aacute; tr&ecirc;n 30 tỉ đồng/m<sup>2</sup>&nbsp;(cao gấp khoảng 12.000 lần mức gi&aacute; khởi điểm)&hellip; dẫn đến việc 36 l&ocirc; đất đấu gi&aacute; kh&ocirc;ng th&agrave;nh c&ocirc;ng.</p>\r\n<p data-id-seo=\"paragraphs-11\">C&ocirc;ng an H&agrave; Nội cho hay, trước dấu hiệu bất thường của cuộc đấu gi&aacute;, l&atilde;nh đạo C&ocirc;ng an H&agrave; Nội đ&atilde; chỉ đạo PC03 phối hợp với c&aacute;c b&ecirc;n li&ecirc;n quan điều tra h&agrave;nh vi c&oacute; dấu hiệu vi phạm ph&aacute;p luật của nh&oacute;m Phạm Ngọc Tuấn.</p>\r\n<p data-id-seo=\"paragraphs-12\">Đồng thời, l&atilde;nh đạo C&ocirc;ng an H&agrave; Nội cũng chỉ đạo c&aacute;c đơn vị nghiệp vụ tăng cường r&agrave; so&aacute;t, kịp thời ph&aacute;t hiện, ngăn chặn v&agrave; xử l&yacute; nghi&ecirc;m đối với c&aacute;c h&agrave;nh vi lợi dụng ch&iacute;nh s&aacute;ch ph&aacute;p luật để vi phạm ph&aacute;p luật trong lĩnh vực đấu gi&aacute; t&agrave;i sản n&oacute;i chung v&agrave; đấu gi&aacute; quyền sử dụng đất n&oacute;i ri&ecirc;ng tr&ecirc;n địa b&agrave;n th&agrave;nh phố.</p>\r\n<p data-id-seo=\"paragraphs-13\">PC03&nbsp;<a class=\"link-inline-content\" title=\"C&ocirc;ng an H&agrave; Nội\" href=\"https://thanhnien.vn/thu-mon-tre-cuc-hay-hagl-bat-ngo-danh-bai-clb-cong-an-ha-noi-185241109191849275.htm\" data-rel=\"follow\">C&ocirc;ng an H&agrave; Nội</a>&nbsp;đang củng cố t&agrave;i liệu, chứng cứ để khởi tố vụ &aacute;n, khởi tố bị can, xử l&yacute; nghi&ecirc;m những người li&ecirc;n quan theo quy định.</p>\r\n<p data-id-seo=\"paragraphs-1\">&nbsp;</p>\r\n</div>\r\n</div>', 'ACTIVE', '2024-12-03 14:14:19', '2024-12-03 14:14:19', 5, 19),
(11, '4 mẫu xe máy nhập khẩu rục rịch gia nhập thị trường Việt Nam', '4-mau-xe-may-nhap-khau-ruc-rich-gia-nhap-thi-truong-viet-nam', 'edit-xe-may-moi-2-17327571683878.png', '<h2 class=\"detail-sapo has-audio\" data-role=\"sapo\">Một số mẫu m&atilde; xe m&aacute;y mới thuộc ph&acirc;n kh&uacute;c xe số, xe tay ga rục rịch gia nhập thị trường Việt Nam, đ&aacute;p ứng nhu cầu của nh&oacute;m kh&aacute;ch h&agrave;ng th&iacute;ch sự mới lạ, kh&aacute;c biệt so với những d&ograve;ng xe lắp r&aacute;p trong nước vốn đ&atilde; qu&aacute; phổ biến tr&ecirc;n thị trường.</h2>\r\n<div class=\"detail-cmain\">\r\n<div class=\"detail-content afcbc-body\" data-role=\"content\" data-io-article-url=\"https://thanhnien.vn/4-mau-xe-may-nhap-khau-ruc-rich-gia-nhap-thi-truong-viet-nam-185241127110711157.htm\">\r\n<div data-check-position=\"body_start\">&nbsp;</div>\r\n<p>Sau bước \"chạy đ&agrave;\" kh&ocirc;ng mấy khả quan, thị trường xe m&aacute;y Việt Nam đang từng bước hồi phục khi doanh số b&aacute;n li&ecirc;n tiếp tăng trưởng trong qu&yacute; 2 v&agrave; qu&yacute; 3/2024. Nhu cầu mua sắm&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Xe m&aacute;y\" href=\"https://thanhnien.vn/xe-may.html\" target=\"_blank\" rel=\"noopener\">xe m&aacute;y</a>&nbsp;của người Việt thường gia tăng dịp cuối năm c&agrave;ng mang đến nhiều t&iacute;n hiệu t&iacute;ch cực cho thị trường xe m&aacute;y khi bước v&agrave;o giai đoạn \"chạy nước r&uacute;t\". Sức mua tăng trưởng cũng tạo động lực cho c&aacute;c nh&agrave; sản xuất, ph&acirc;n phối&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"C&oacute; n&ecirc;n để điện thoại, sạc dự ph&ograve;ng trong cốp xe m&aacute;y?\" href=\"https://thanhnien.vn/co-nen-de-dien-thoai-sac-du-phong-trong-cop-xe-may-185241024082207004.htm\" target=\"_blank\" rel=\"noopener\">xe m&aacute;y</a>&nbsp;tung ra thị trường nhiều mẫu m&atilde; mới.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/edit-honda-scoopy-2025-ra-mat-7.png\" alt=\"\" width=\"640\" height=\"366\" />\r\n<figcaption>Từ nay đến đầu năm 2025, nhiều mẫu xe m&aacute;y nhập khẩu từ c&aacute;c quốc gia trong <br />khu vực Đ&ocirc;ng Nam &Aacute; hay Nhật Bản, ch&acirc;u &Acirc;u sẽ được đưa về Việt Nam</figcaption>\r\n</figure>\r\n<p>C&ugrave;ng với c&aacute;c d&ograve;ng xe lắp r&aacute;p trong nước, một số mẫu m&atilde; xe m&aacute;y nhập khẩu vốn h&uacute;t kh&aacute;ch hay mang t&iacute;nh chất độc lạ, số lượng hạn chế&hellip; cũng đang tr&ecirc;n đường về Việt Nam. Theo nguồn tin của&nbsp;<em>Thanh Ni&ecirc;n</em>, trong giai đoạn từ nay đến đầu năm 2025, nhiều mẫu&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Hai phi&ecirc;n bản cuối c&ugrave;ng của Honda Super Cub 50 sản xuất giới hạn 2.000 xe\" href=\"https://thanhnien.vn/hai-phien-ban-cuoi-cung-cua-honda-super-cub-50-san-xuat-gioi-han-2000-xe-185241102130855983.htm\" target=\"_blank\" rel=\"noopener\">xe m&aacute;y</a>&nbsp;nhập khẩu từ c&aacute;c quốc gia trong khu vực Đ&ocirc;ng Nam &Aacute; hay Nhật Bản, ch&acirc;u &Acirc;u sẽ được đưa về Việt Nam. C&aacute;c mẫu xe n&agrave;y phần lớn thuộc thương hiệu Honda, đ&aacute;p ứng nhu cầu của nh&oacute;m kh&aacute;ch h&agrave;ng th&iacute;ch sự mới lạ, kh&aacute;c biệt so với những d&ograve;ng xe lắp r&aacute;p trong nước vốn đ&atilde; qu&aacute; phổ biến tr&ecirc;n thị trường.</p>\r\n<p>Dưới đ&acirc;y l&agrave; 4 mẫu xe m&aacute;y nhập khẩu rục rịch gia nhập thị trường Việt Nam:</p>\r\n<h2>Honda Scoopy thế hệ mới</h2>\r\n<p>Thế hệ mới nhất của d&ograve;ng xe tay ga cỡ nhỏ -&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Honda Scoopy\" href=\"https://thanhnien.vn/honda-scoopy.html\" target=\"_blank\" rel=\"noopener\">Honda Scoopy</a>&nbsp;với h&agrave;ng loạt thay đổi từ kiểu d&aacute;ng thiết kế cho đến c&ocirc;ng nghệ, t&iacute;nh năng&hellip; vừa được Honda tung ra thị trường Đ&ocirc;ng Nam &Aacute; v&agrave;o đầu th&aacute;ng 11.2024 v&agrave; sẽ về Việt Nam trong giai đoạn cuối năm.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/edit-honda-scoopy-2025-ra-mat-4.png\" alt=\"\" width=\"640\" height=\"428\" />\r\n<figcaption>Thế hệ mới nhất của d&ograve;ng xe tay ga cỡ nhỏ - Honda Scoopy với h&agrave;ng loạt thay đổi từ kiểu <br />d&aacute;ng thiết kế cho đến c&ocirc;ng nghệ, t&iacute;nh năng</figcaption>\r\n</figure>\r\n<p><a class=\"seo-suggest-link link-inline-content\" title=\"Honda Scoopy thế hệ mới c&oacute; nhiều thay đổi, rục rịch về Việt Nam\" href=\"https://thanhnien.vn/honda-scoopy-the-he-moi-co-nhieu-thay-doi-ruc-rich-ve-viet-nam-185241106093341702.htm\" target=\"_blank\" rel=\"noopener\">Honda Scoopy</a>&nbsp;mới thuộc thế hệ thứ 6, sản xuất tại Indonesia vừa được PT Astra Honda Motor (AHM) - C&ocirc;ng ty li&ecirc;n doanh giữa Honda v&agrave; đối t&aacute;c ở Indonesia tung ra thị trường với 4 phi&ecirc;n bản kh&aacute;c nhau. So với xe đời cũ đang ph&acirc;n phối tại Việt Nam,&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Khung sườn eSAF tr&ecirc;n xe m&aacute;y Honda bị tố dễ gỉ s&eacute;t, c&oacute; nguy cơ g&atilde;y\" href=\"https://thanhnien.vn/khung-suon-esaf-tren-xe-may-honda-bi-to-de-gi-set-co-nguy-co-gay-185230829141758966.htm\" target=\"_blank\" rel=\"noopener\">Honda Scoopy</a>&nbsp;mới c&oacute; kh&aacute; nhiều thay đổi về kiểu d&aacute;ng thiết kế, bổ sung trang bị, t&iacute;nh năng nhưng vẫn sử dụng động cơ eSP 110cc SOHC t&iacute;ch hợp c&ocirc;ng nghệ PGM-FI.</p>\r\n<p>Tại Indonesia, Honda Scoopy thế hệ mới do PT Astra Honda Motor (AHM) ph&acirc;n phối 4 phi&ecirc;n bản với 8 lựa chọn m&agrave;u sắc kh&aacute;c nhau, gi&aacute; b&aacute;n từ 22,5 - 23,3 triệu rupiah, tương đương khoảng 35,6 - 36,8 triệu đồng. Mẫu xe n&agrave;y dự kiến sẽ gia nhập thị trường Việt Nam v&agrave;o cuối năm nay th&ocirc;ng qua c&aacute;c cửa h&agrave;ng kh&ocirc;ng ch&iacute;nh h&atilde;ng chuy&ecirc;n kinh doanh&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"D&acirc;n mạng đ&ograve;i phạt nặng xe m&aacute;y chở 3 ph&oacute;ng nhanh, lạng l&aacute;ch tr&ecirc;n cao tốc\" href=\"https://thanhnien.vn/dan-mang-doi-phat-nang-xe-may-cho-3-phong-nhanh-lang-lach-tren-cao-toc-185241126051523882.htm\" target=\"_blank\" rel=\"noopener\">xe m&aacute;y</a>&nbsp;nhập khẩu tại Việt Nam.</p>\r\n<h2>Xe m&aacute;y số Honda Super Cub 50 Final Edition</h2>\r\n<p>Phi&ecirc;n bản cuối c&ugrave;ng của d&ograve;ng&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Tuổi thọ xe m&aacute;y v&agrave; mục ti&ecirc;u ph&aacute;t triển bền vững của Việt Nam\" href=\"https://thanhnien.vn/tuoi-tho-xe-may-va-muc-tieu-phat-trien-ben-vung-cua-viet-nam-185241121090714689.htm\" target=\"_blank\" rel=\"noopener\">xe m&aacute;y</a>&nbsp;số Honda Super Cub 50 mang t&ecirc;n&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Honda Super Cub 50 Final Edition\" href=\"https://thanhnien.vn/honda-super-cub-50-final-edition.html\" target=\"_blank\" rel=\"noopener\">Honda Super Cub 50 Final Edition</a>&nbsp;sản xuất giới hạn 2.000 xe đang rục rịch gia nhập thị trường Việt Nam. Tại thị trường Nhật Bản, Honda bắt đầu nhận đơn đặt h&agrave;ng Super Cub 50 Final Edition bắt đầu từ ng&agrave;y 8 đến 24.11.2024.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/honda-super-cub-50-4-17305275536.png\" alt=\"\" width=\"640\" height=\"461\" />\r\n<figcaption>Honda Super Cub 50 Final Edition sẽ được đưa về Việt Nam v&agrave;o cuối năm 2024 hoặc đầu <br />năm 2025</figcaption>\r\n</figure>\r\n<p>Theo nguồn tin của&nbsp;<em>Thanh Ni&ecirc;n</em>, sau khi ph&acirc;n phối tại thị trường Nhật Bản,&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Honda Super Cub 50 Final Edition bản giới hạn 2.000 xe rục rịch về Việt Nam\" href=\"https://thanhnien.vn/honda-super-cub-50-final-edition-ban-gioi-han-2000-xe-ruc-rich-ve-viet-nam-185241111111348114.htm\" target=\"_blank\" rel=\"noopener\">Honda Super Cub 50 Final Edition</a>&nbsp;sẽ được đưa về Việt Nam v&agrave;o cuối năm 2024 hoặc đầu năm 2025. Tuy nhi&ecirc;n, mẫu xe n&agrave;y kh&ocirc;ng do Honda Việt Nam (HVN) nhập khẩu, ph&acirc;n phối m&agrave; được đưa về, mở b&aacute;n th&ocirc;ng qua một cửa h&agrave;ng kinh doanh xe kh&ocirc;ng ch&iacute;nh h&atilde;ng tại TP.HCM nhập khẩu.</p>\r\n<p>Honda Super Cub 50 Final Edition mang phong c&aacute;ch ho&agrave;i cổ với th&acirc;n xe phối m&agrave;u sơn trắng v&agrave; xanh Bonnie, gợi nhớ đến chiếc Super Cub ng&agrave;y xưa. M&agrave;u sơn n&agrave;y kết hợp c&aacute;c điểm nhấn m&agrave;u bạc, mạ cr&ocirc;m. Xe sử dụng động cơ xi-lanh đơn, dung t&iacute;ch 49cc c&oacute; c&ocirc;ng suất 3,6 m&atilde; lực v&agrave; m&ocirc;-men xoắn cực đại 3,8 Nm. Honda xuất xưởng giới hạn 2.000 xe Super Cub 50 Final Edition, mỗi chiếc c&oacute; gi&aacute; 270.000 yen (khoảng 1.765 USD).</p>\r\n<h2>Honda Wave 125i \'Made in Thailand\'</h2>\r\n<p>Đ&acirc;y l&agrave; mẫu xe m&aacute;y số tầm trung kh&ocirc;ng chỉ tạo sức h&uacute;t tại Th&aacute;i Lan m&agrave; c&ograve;n được nhiều kh&aacute;ch h&agrave;ng Việt Nam quan t&acirc;m. Sản xuất tại nh&agrave; m&aacute;y ở Th&aacute;i Lan,&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Honda Wave 125i\" href=\"https://thanhnien.vn/honda-wave-125i.html\" target=\"_blank\" rel=\"noopener\">Honda Wave 125i</a>&nbsp;vừa được Honda cập nhật phi&ecirc;n bản 2025 với một số thay đổi về diện mạo, m&agrave;u sắc trong khi trang bị, t&iacute;nh năng v&agrave; động cơ vẫn giữ nguy&ecirc;n.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/edit-xe-may-moi-2-17327571683878.png\" alt=\"\" width=\"640\" height=\"394\" />\r\n<figcaption>Honda Wave 125i vừa được Honda cập nhật phi&ecirc;n bản 2025 với một số thay đổi về diện <br />mạo, m&agrave;u sắc</figcaption>\r\n</figure>\r\n<p>Theo t&igrave;m hiểu của PV&nbsp;<em>Thanh Ni&ecirc;n</em>,&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"6 điểm mới tr&ecirc;n Honda Wave 125i &lsquo;Made in Thailand&rsquo; so với đời cũ\" href=\"https://thanhnien.vn/6-diem-moi-tren-honda-wave-125i-made-in-thailand-so-voi-doi-cu-1851507222.htm\" target=\"_blank\" rel=\"noopener\">Honda Wave 125i</a>&nbsp;2025 vừa tr&igrave;nh l&agrave;ng tại Th&aacute;i Lan chỉ l&agrave; bản cập nhật m&agrave;u sắc, c&ocirc;ng nghệ chứ kh&ocirc;ng phải l&agrave; phi&ecirc;n bản n&acirc;ng cấp.&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Honda Future 125 FI mới tại Việt Nam kh&aacute;c Wave \'Th&aacute;i Lan\', gi&aacute; từ 30,5 triệu đồng\" href=\"https://thanhnien.vn/honda-future-125-fi-moi-tai-viet-nam-khac-wave-thai-lan-gia-tu-305-trieu-dong-18524010322111404.htm\" target=\"_blank\" rel=\"noopener\">Honda Wave 125i</a>&nbsp;2025 vẫn d&ugrave;ng động cơ xăng dung t&iacute;ch 125 ph&acirc;n khối, nhưng kh&aacute;c với mẫu cũ đang ph&acirc;n phối tại Việt Nam, động cơ tr&ecirc;n Honda Wave 125i 2024 ứng dụng c&ocirc;ng nghệ Smart Engine, bổ sung hệ thống Piston Oil Jet gi&uacute;p giảm nhiệt, giảm m&agrave;i m&ograve;n, bền bỉ, k&eacute;o d&agrave;i thời gian sử dụng. Theo th&ocirc;ng số nh&agrave; sản xuất c&ocirc;ng bố, động cơ tr&ecirc;n Honda Wave 125i 2025 cho mức ti&ecirc;u hao nhi&ecirc;n liệu chỉ 1,4 l&iacute;t/100 km.</p>\r\n<p>Tại Th&aacute;i Lan, Honda Wave 125i 2025 c&oacute; 2 phi&ecirc;n bản, gồm bản ti&ecirc;u chuẩn d&ugrave;ng m&acirc;m nan hoa v&agrave; bản cao cấp d&ugrave;ng m&acirc;m đ&uacute;c hợp kim. Theo th&ocirc;ng tin Honda Th&aacute;i Lan c&ocirc;ng bố, Honda Wave 125i 2025 c&oacute; gi&aacute; b&aacute;n từ 56.000 - 58.200 baht, tương đương 38,5 - 39,9 triệu đồng.</p>\r\n<h2>Italjet Dragster 300 Malossi</h2>\r\n<p>Một mẫu xe c&ograve;n kh&aacute; xa lạ với phần lớn kh&aacute;ch h&agrave;ng Việt Nam, nhưng tại ch&acirc;u &Acirc;u cũng như nhiều thị trường kh&aacute;c tr&ecirc;n&nbsp;<a class=\"VCCAutoLink link-inline-content\" title=\"thế giới\" href=\"https://thanhnien.vn/the-gioi.htm\" target=\"_blank\" rel=\"noopener\" data-id-autolink=\"7435\">thế giới</a>,&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Italjet Dragster\" href=\"https://thanhnien.vn/italjet-dragster.html\" target=\"_blank\" rel=\"noopener\">Italjet Dragster</a>&nbsp;300 Malossi từng thu h&uacute;t sự ch&uacute; &yacute; của giới mộ điệu xe với kiểu d&aacute;ng ph&aacute; c&aacute;ch cũng khả năng vận h&agrave;nh mạnh mẽ.</p>\r\n<figure class=\"VCSortableInPreviewMode\">\r\n<div>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/edit-xe-may-moi-3-17327572182591.png\" alt=\"\" width=\"640\" height=\"379\" />\r\n<figcaption>\r\n<p data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">Italjet Dragster 300 Malossi sẽ về Việt Nam v&agrave;o cuối th&aacute;ng 11 hoặc đầu th&aacute;ng 12.2024</p>\r\n</figcaption>\r\n</figure>\r\n<p>Phi&ecirc;n bản&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Xe tay ga Italjet Dragster 2022 thiết kế &lsquo;độc lạ&rsquo; gi&aacute; hơn 150 triệu đồng\" href=\"https://thanhnien.vn/xe-tay-ga-italjet-dragster-2022-thiet-ke-doc-la-gia-hon-150-trieu-dong-1851508205.htm\" target=\"_blank\" rel=\"noopener\">Italjet Dragster</a>&nbsp;300 Malossi sở hữu vẻ ngo&agrave;i sặc sỡ với c&aacute;c m&agrave;u sắc đỏ, cam, xanh dạ quang,... Đ&acirc;y l&agrave; phi&ecirc;n bản đến từ sự hợp t&aacute;c giữa nh&agrave; sản xuất d&ograve;ng xe&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Vừa mở b&aacute;n tại Đ&ocirc;ng Nam &Aacute;, Italjet Dragster bị h&atilde;ng xe Trung Quốc l&agrave;m nh&aacute;i\" href=\"https://thanhnien.vn/vua-mo-ban-tai-dong-nam-a-italjet-dragster-bi-hang-xe-trung-quoc-lam-nhai-1851273762.htm\" target=\"_blank\" rel=\"noopener\">Italjet Dragster</a>&nbsp;v&agrave; thương hiệu phụ t&ugrave;ng Malossi nổi tiếng thế giới. Mẫu xe n&agrave;y sở hữu h&agrave;ng loạt trang bị \"xịn\" như phuộc nh&uacute;n đến từ thương hiệu Bitubo, heo thắng Brembo 2 piston trước sau, c&ocirc;ng nghệ b&oacute; cứng phanh ABS,... Đặc biệt,&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Xe tay ga &lsquo;độc, lạ&rsquo; Italjet Dragster gia nhập thị trường Đ&ocirc;ng Nam &Aacute;\" href=\"https://thanhnien.vn/xe-tay-ga-doc-la-italjet-dragster-gia-nhap-thi-truong-dong-nam-a-1851273585.htm\" target=\"_blank\" rel=\"noopener\">Italjet Dragster</a>&nbsp;300 Malossi sở hữu khối động cơ dung t&iacute;ch 278cc ho&agrave;n to&agrave;n mới, được ph&aacute;t triển từ nền tảng cam đ&ocirc;i DOHC 4 van cho c&ocirc;ng suất tối đa 23,4 m&atilde; lực, m&ocirc;-men xoắn cực đại 26 Nm tại 6.250 v&ograve;ng/ph&uacute;t.</p>\r\n<p>Italjet Dragster 300 Malossi sẽ về Việt Nam v&agrave;o cuối th&aacute;ng 11 hoặc đầu th&aacute;ng 12.2024. Hiện tại, ph&iacute;a đơn vị nhập khẩu, ph&acirc;n phối tại Việt Nam đ&atilde; bắt đầu nhận đơn đặt h&agrave;ng với mẫu xe n&agrave;y.</p>\r\n</div>\r\n</figure>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>', 'ACTIVE', '2024-12-03 14:20:23', '2024-12-03 14:20:23', 3, 19);
INSERT INTO `posts` (`id`, `title`, `slug`, `image`, `content`, `status`, `created_at`, `updated_at`, `category_id`, `user_id`) VALUES
(12, 'Lần đầu tiên HLV Kim Sang-sik lên tiếng về Nguyễn Xuân Son: Đội tuyển Việt Nam sẽ…', 'lan-dau-tien-hlv-kim-sang-sik-len-tieng-ve-nguyen-xuan-son-doi-tuyen-viet-nam-se', 'anh-man-hinh-2024-12-03-luc-1613.png', '<h2 class=\"detail-sapo has-audio\" data-role=\"sapo\">HLV Kim Sang-sik tin rằng Nguyễn Xu&acirc;n Son sẽ mang lại th&ecirc;m nhiều phương &aacute;n tấn c&ocirc;ng cho đội tuyển Việt Nam ở AFF Cup 2024.</h2>\r\n<div class=\"detail-cmain\">\r\n<div class=\"detail-content afcbc-body\" data-role=\"content\" data-io-article-url=\"https://thanhnien.vn/lan-dau-tien-hlv-kim-sang-sik-len-tieng-ve-nguyen-xuan-son-185241203161631963.htm\">\r\n<div data-check-position=\"body_start\">&nbsp;</div>\r\n<p data-id-seo=\"paragraphs-0\">Chiều 3.12, Li&ecirc;n đo&agrave;n B&oacute;ng đ&aacute; Việt Nam c&ocirc;ng bố 3 cầu thủ&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"\'Mỏ v&agrave;ng\' ở CLB Nam Định tỏa s&aacute;ng, HLV Kim Sang-sik rũ bỏ được bất an\" href=\"https://thanhnien.vn/mo-vang-o-clb-nam-dinh-toa-sang-hlv-kim-sang-sik-ru-bo-duoc-bat-an-185241128121135134.htm\" target=\"_blank\" rel=\"noopener\">CLB Nam Định</a>&nbsp;được triệu tập bổ sung l&ecirc;n đội tuyển Việt Nam để chuẩn bị thi đấu tại&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"HLV Kim Sang-sik gạch t&ecirc;n ai khỏi đội tuyển Việt Nam?\" href=\"https://thanhnien.vn/hlv-kim-sang-sik-gach-ten-ai-khoi-doi-tuyen-viet-nam-185241203152604997.htm\" target=\"_blank\" rel=\"noopener\">AFF Cup 2024</a>. Đ&oacute; l&agrave; Nguyễn Văn Vĩ, Nguyễn Văn To&agrave;n v&agrave;&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"N&oacute;ng: Nguyễn Xu&acirc;n Son được ph&eacute;p kho&aacute;c &aacute;o đội tuyển Việt Nam từ ng&agrave;y 21.12\" href=\"https://thanhnien.vn/nong-nguyen-xuan-son-duoc-phep-khoac-ao-doi-tuyen-viet-nam-tu-ngay-2112-185241202094952349.htm\" target=\"_blank\" rel=\"noopener\">Nguyễn Xu&acirc;n Son</a>, tiền đạo gốc Brazil. Bộ ba cầu thủ n&agrave;y sẽ hội qu&acirc;n c&ugrave;ng đội tuyển v&agrave;o ng&agrave;y 5.12 tới, sau khi ho&agrave;n th&agrave;nh thi đấu cho CLB Nam Định tại v&ograve;ng bảng AFC Champions League Two 2024-2025.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/anh-man-hinh-2024-12-03-luc-1613.png\" alt=\"\" width=\"640\" height=\"419\" />\r\n<figcaption>HLV Kim Sang-sik đặt nhiều kỳ vọng v&agrave;o Xu&acirc;n Son</figcaption>\r\n</figure>\r\n<p>HLV Kim Sang-sik hy vọng với sự g&oacute;p mặt của Xu&acirc;n Son, đội tuyển sẽ c&oacute; th&ecirc;m nhiều phương &aacute;n tấn c&ocirc;ng hiệu quả hơn. Nh&agrave; cầm qu&acirc;n người H&agrave;n Quốc cũng tin tưởng v&agrave;o sự h&ograve;a nhập của Nguyễn Xu&acirc;n Son với đội tuyển, bởi cầu thủ n&agrave;y đ&atilde; trải qua 5 năm sinh sống tại Việt Nam v&agrave; thi đấu trong m&ocirc;i trường V-League. B&ecirc;n cạnh đ&oacute;, Son vẫn c&oacute; quỹ thời gian để bắt nhịp với đội tuyển trước khi ch&iacute;nh thức được ra s&acirc;n thi đấu sau ng&agrave;y 20.12.2024 theo quy định của FIFA về thời gian cư tr&uacute; đối với cầu thủ nhập tịch.<img style=\"display: block; margin-left: auto; margin-right: auto;\" src=\"/storage/photos/19/post/img8049-1733215197144629655864.png\" alt=\"\" width=\"640\" height=\"360\" /></p>\r\n<p data-id-seo=\"paragraphs-6\">Về ph&iacute;a Nguyễn Xu&acirc;n Son, tiền đạo gốc Brazil đ&atilde; ho&agrave;n th&agrave;nh thủ tục nhập quốc tịch Việt Nam v&agrave; sẵn s&agrave;ng cống hiến cho m&agrave;u &aacute;o đội tuyển Việt Nam. Anh cũng đang thể hiện phong độ rất cao trong m&agrave;u &aacute;o CLB Nam Định v&agrave; được đ&aacute;nh gi&aacute; l&agrave; một trong những ch&acirc;n s&uacute;t h&agrave;ng đầu tại V-League.&nbsp;</p>\r\n<p data-id-seo=\"paragraphs-7\">Sự g&oacute;p mặt của Nguyễn Xu&acirc;n Son được kỳ vọng sẽ tăng cường sức mạnh tấn c&ocirc;ng cho đội tuyển Việt Nam trong cuộc đua đầy thử th&aacute;ch tại giải v&ocirc; địch Đ&ocirc;ng Nam &Aacute; 2024 sắp tới. Bản th&acirc;n Nguyễn Xu&acirc;n Son trong c&aacute;c lần tiếp x&uacute;c với truyền th&ocirc;ng cũng đều b&agrave;y tỏ niềm kh&aacute;t khao được thi đấu cho đội tuyển Việt Nam. \"Được thi đấu cho đội tuyển Việt Nam l&agrave; cơ hội lớn nhất trong cuộc đời t&ocirc;i. T&ocirc;i cảm thấy rất vui mừng v&agrave; tự h&agrave;o khi được kho&aacute;c &aacute;o đội tuyển quốc gia. T&ocirc;i sẽ cố gắng hết m&igrave;nh để đ&oacute;ng g&oacute;p v&agrave;o th&agrave;nh c&ocirc;ng của đội tuyển\", Nguyễn Xu&acirc;n Son chia sẻ.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/img8053-1733216135219763315431.png\" alt=\"\" width=\"640\" height=\"417\" />\r\n<figcaption>Trong những ng&agrave;y tới, c&ugrave;ng với 30 cầu thủ trong dang s&aacute;ch sơ bộ được triệu tập trước đ&oacute;,<br />chiến lược gia người H&agrave;n Quốc cũng sẽ c&oacute; th&ecirc;m sự g&oacute;p mặt của 3 cầu thủ thuộc bi&ecirc;n chế <br />CLB Nam Định sau khi đội b&oacute;ng n&agrave;y ho&agrave;n tất trận đấu v&ograve;ng bảng AFC Champions League<br />Two ở Th&aacute;i Lan v&agrave;o ng&agrave;y 4.12. Đ&oacute; l&agrave; hậu vệ Nguyễn Văn Vĩ, tiền đạo Nguyễn Xu&acirc;n Son v&agrave;<br />tiền đạo Nguyễn Văn To&agrave;n.Theo kế hoạch, đội tuyển Việt Nam sẽ tập luyện ở Việt Tr&igrave; cho<br />đến ng&agrave;y 6.12 sẽ l&ecirc;n đường sang L&agrave;o để chuẩn bị cho trận khai m&agrave;n tại bảng B giải ASEAN<br />Mitsubishi Electric Cup 2024 (AFF Cup), diễn ra v&agrave;o ng&agrave;y 9.12. Sau trận đấu n&agrave;y, thầy tr&ograve;<br />HLV Kim Sang Sik sẽ tiếp tục trở về Ph&uacute; Thọ để chuẩn bị cho cuộc đối đầu với đội tuyển<br />Indonesia v&agrave;o l&uacute;c 20 giờ ng&agrave;y 15.12 tr&ecirc;n SVĐ Việt Tr&igrave;.</figcaption>\r\n</figure>\r\n<p data-id-seo=\"paragraphs-10\">Trong số 3 c&aacute;i t&ecirc;n n&oacute;i tr&ecirc;n, Nguyễn Văn To&agrave;n l&agrave; cầu thủ d&agrave;y dạn kinh nghiệm với rất nhiều lần kho&aacute;c &aacute;o đội tuyển quốc gia. Nguyễn Văn Vĩ cũng từng được một lần được triệu tập l&ecirc;n đội tuyển Việt Nam v&agrave; g&oacute;p mặt trong trận đấu giao hữu với đội tuyển Afghanistan hồi đầu năm 2022. Với m&agrave;n tr&igrave;nh diễn ấn tượng trong m&agrave;u &aacute;o CLB Nam Định thời gian qua, Văn Vĩ đang được đ&aacute;nh gi&aacute; l&agrave; một trong những hậu vệ tr&aacute;i xuất sắc nhất tại V.League 2024-2025 v&agrave; ho&agrave;n to&agrave;n xứng đ&aacute;ng được trao cơ hội trong đội h&igrave;nh đội tuyển Việt Nam tham dự giải v&ocirc; địch Đ&ocirc;ng Nam &Aacute; 2024.</p>\r\n<p data-id-seo=\"paragraphs-11\">Theo kế hoạch, đội tuyển Việt Nam sẽ duy tr&igrave; tập luyện tại Việt Tr&igrave; cho đến ng&agrave;y 6.12 sẽ di chuyển sang L&agrave;o để chuẩn bị cho trận khai m&agrave;n tại bảng B, giải v&ocirc; địch Đ&ocirc;ng Nam &Aacute; 2024. Trước khi l&ecirc;n đường, HLV Kim Sang Sik sẽ c&ocirc;ng bố danh s&aacute;ch ch&iacute;nh thức gồm 26 cầu thủ theo quy định của điều lệ giải.</p>\r\n<p data-id-seo=\"paragraphs-7\">&nbsp;</p>\r\n<p data-id-seo=\"paragraphs-0\">&nbsp;</p>\r\n</div>\r\n</div>', 'ACTIVE', '2024-12-03 14:24:50', '2024-12-03 14:24:50', 1, 19),
(14, 'Đại học Huế có 5 tân phó giáo sư dưới 40 tuổi', 'dai-hoc-hue-co-5-tan-pho-giao-su-duoi-40-tuoi', 'base64-17332208808701503066536.png', '<h2 class=\"detail-sapo has-audio\" data-role=\"sapo\">Đại học Huế vừa c&oacute; 21 nh&agrave; gi&aacute;o v&agrave; 1 giảng vi&ecirc;n thỉnh giảng được c&ocirc;ng nhận đạt ti&ecirc;u chuẩn chức danh ph&oacute; gi&aacute;o sư, trong đ&oacute; c&oacute; 5 t&acirc;n ph&oacute; gi&aacute;o sư dưới 40 tuổi.</h2>\r\n<div class=\"detail-cmain\">\r\n<div class=\"detail-content afcbc-body\" data-role=\"content\" data-io-article-url=\"https://thanhnien.vn/dai-hoc-hue-co-5-tan-pho-giao-su-duoi-40-tuoi-185241203164254734.htm\">\r\n<div data-check-position=\"body_start\">&nbsp;</div>\r\n<p>Ng&agrave;y 3.12,&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Đại học huế\" href=\"https://thanhnien.vn/dai-hoc-hue.html\" target=\"_blank\" rel=\"noopener\">Đại học Huế</a>&nbsp;tổ chức lễ&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"c&ocirc;ng bố quyết định\" href=\"https://thanhnien.vn/cong-bo-quyet-dinh.html\" target=\"_blank\" rel=\"noopener\">c&ocirc;ng bố quyết định</a>, trao giấy chứng nhận đạt ti&ecirc;u chuẩn chức danh&nbsp;<a class=\"link-inline-content\" title=\"ph&oacute; gi&aacute;o sư\" href=\"https://thanhnien.vn/ty-le-giao-su-pho-giao-su-tai-cac-truong-dh-ra-sao-185241121214455043.htm\" data-rel=\"follow\">ph&oacute; gi&aacute;o sư</a>&nbsp;năm 2024 cho 22&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Nh&agrave; gi&aacute;o\" href=\"https://thanhnien.vn/nha-giao.html\" target=\"_blank\" rel=\"noopener\">nh&agrave; gi&aacute;o</a>, giảng vi&ecirc;n thỉnh giảng thuộc đơn vị.</p>\r\n<p>Đ&aacute;ng ch&uacute; &yacute;, trong đợt c&ocirc;ng nhận đạt ti&ecirc;u chuẩn chức danh gi&aacute;o sư, ph&oacute; gi&aacute;o sư năm nay, Đại học Huế c&oacute; 5 nh&agrave; gi&aacute;o được c&ocirc;ng nhận đạt ti&ecirc;u chuẩn chức danh ph&oacute; gi&aacute;o sư trước tuổi 40.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/base64-17332207550651517172980.png\" alt=\"\" width=\"640\" height=\"328\" />\r\n<figcaption>\r\n<p class=\"\" data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">L&atilde;nh đạo Đại học Huế trao giấy chứng nhận cho 22 t&acirc;n ph&oacute; gi&aacute;o sư năm 2024</p>\r\n</figcaption>\r\n</figure>\r\n<p>Những t&acirc;n ph&oacute; gi&aacute;o sư trẻ tuổi n&agrave;y gồm: nh&agrave; gi&aacute;o Trung Hiếu (37 tuổi, khoa H&oacute;a học, Trường đại học Khoa học); nh&agrave; gi&aacute;o Trần Viết Long (38 tuổi, Trưởng ph&ograve;ng Đ&agrave;o tạo, Trường đại học Luật); nh&agrave; gi&aacute;o Đinh Qu&yacute; Hương (38 tuổi, khoa H&oacute;a học, Trường đại học Sư phạm); nh&agrave; gi&aacute;o L&ecirc; Văn H&ograve;a (39 tuổi, Trưởng khoa Quản l&yacute; sự kiện v&agrave; c&ocirc;ng nghệ truyền th&ocirc;ng, Trường Du lịch); nh&agrave; gi&aacute;o Nguyễn Thanh T&ugrave;ng (39 tuổi, khoa Cơ bản, Trường đại học Y - Dược).</p>\r\n<p>Dịp n&agrave;y, Gi&aacute;m đốc Đại học Huế cũng tặng giấy khen cho 5 t&acirc;n ph&oacute; gi&aacute;o sư dưới 40 tuổi.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/base64-17332208808701503066536.png\" alt=\"\" width=\"640\" height=\"387\" />\r\n<figcaption>\r\n<p class=\"\" data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">Gi&aacute;m đốc Đại học Huế tặng tặng giấy khen cho 5 nh&agrave; gi&aacute;o đạt chức danh gi&aacute;o sư dưới 40</p>\r\n<p class=\"\" data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">tuổi</p>\r\n<div class=\"PhotoCMS_Author\">&nbsp;</div>\r\n</figcaption>\r\n</figure>\r\n<p>Thay mặt 22 t&acirc;n ph&oacute; gi&aacute;o sư của Đại học Huế ph&aacute;t biểu tại lễ c&ocirc;ng bố, PGS-TS Nguyễn Thanh H&ugrave;ng, Ph&oacute; hiệu trưởng Trường đại học Sư phạm, Đại học Huế, chia sẻ về niềm tự h&agrave;o, tr&aacute;ch nhiệm v&agrave; sự cống hiến.</p>\r\n<p>&Ocirc;ng H&ugrave;ng tự h&agrave;o v&igrave; những kết quả h&ocirc;m nay, được trưởng th&agrave;nh từ m&ocirc;i trường của Đại học Huế. Sau khi đạt chức danh ph&oacute; gi&aacute;o sư, &ocirc;ng cũng thấy được tr&aacute;ch nhiệm của m&igrave;nh trong c&ocirc;ng t&aacute;c giảng dạy.</p>\r\n<p>T&acirc;n ph&oacute; gi&aacute;o sư n&agrave;y cho rằng cần thay đổi c&aacute;ch thức giảng dạy, trong đ&oacute; phải tập trung ứng dụng c&ocirc;ng nghệ th&ocirc;ng tin. Đồng thời, cần tham gia v&agrave;o những c&ocirc;ng tr&igrave;nh nghi&ecirc;n cứu khoa học; dẫn dắt chuy&ecirc;n m&ocirc;n li&ecirc;n quan đến lĩnh vực, ng&agrave;nh nghề m&agrave; c&aacute;c t&acirc;n ph&oacute; gi&aacute;o sư tham gia; đ&agrave;o tạo, hướng dẫn thế hệ trẻ tiếp theo trong đơn vị nh&agrave; trường&hellip;</p>\r\n<p>Sau c&ugrave;ng, đ&oacute; l&agrave; sự cống hiến sau khi đạt chức danh ph&oacute; gi&aacute;o sư.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/base64-1733220615738207672804.png\" alt=\"\" width=\"640\" height=\"446\" />\r\n<figcaption>\r\n<p class=\"\" data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">PGS-TS Nguyễn Thanh H&ugrave;ng, Ph&oacute; hiệu trưởng Trường đại học Sư phạm, Đại học Huế,</p>\r\n<p class=\"\" data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">ph&aacute;t biểu tại lễ c&ocirc;ng nhận</p>\r\n<div class=\"PhotoCMS_Author\">&nbsp;</div>\r\n</figcaption>\r\n</figure>\r\n<p>\"Ch&uacute;ng t&ocirc;i sẽ tiếp tục cống hiến trong lĩnh vực đ&agrave;o tạo của m&igrave;nh, x&acirc;y dựng đơn vị ng&agrave;y c&agrave;ng ph&aacute;t triển, lớn lao hơn l&agrave; ph&aacute;t triển Đại học Huế. V&agrave; xa hơn nữa l&agrave; g&oacute;p sức m&igrave;nh để Đại học Huế trở th&agrave;nh đại học quốc gia trong tương lai\", PGS-TS Nguyễn Thanh H&ugrave;ng n&oacute;i.</p>\r\n<p>Ph&aacute;t biểu ch&uacute;c mừng c&aacute;c t&acirc;n ph&oacute; gi&aacute;o sư, PGS-TS L&ecirc; Anh Phương, Gi&aacute;m đốc Đại học Huế, khẳng định Đại học Huế sẽ lu&ocirc;n tạo mọi điều kiện để c&aacute;c nh&agrave; gi&aacute;o hoạt động tốt, c&oacute; m&ocirc;i trường l&agrave;m việc tốt, ph&aacute;t huy năng lực bản th&acirc;n. &Ocirc;ng cũng khẳng định sẽ sớm đưa Đại học Huế trở th&agrave;nh đại học quốc gia.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>', 'ACTIVE', '2024-12-03 14:32:53', '2024-12-03 14:32:53', 8, 19),
(15, 'Sách bị phản ứng vì sai chính tả, Hoa hậu Kỳ Duyên nói gì?', 'sach-bi-phan-ung-vi-sai-chinh-ta-hoa-hau-ky-duyen-noi-gi', 'ky-duyen-2-173322139383559490919.png', '<h2 class=\"detail-sapo has-audio\" data-role=\"sapo\">Trước những phản ứng của bạn đọc khi dự &aacute;n mini photobook c&oacute; nhiều lỗi sai, Hoa hậu Kỳ Duy&ecirc;n v&agrave; Dược sĩ Tiến vừa l&ecirc;n tiếng phản hồi.</h2>\r\n<div class=\"detail-cmain\">\r\n<div class=\"detail-content afcbc-body\" data-role=\"content\" data-io-article-url=\"https://thanhnien.vn/sach-bi-phan-ung-vi-sai-chinh-ta-hoa-hau-ky-duyen-noi-gi-185241203173412434.htm\">\r\n<div data-check-position=\"body_start\">&nbsp;</div>\r\n<p>Sau khi trở về từ cuộc thi&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"&Aacute; hậu Hương Ly l&ecirc;n tiếng về tin đồn hẹn h&ograve; Chủ tịch Miss Universe Vietnam\" href=\"https://thanhnien.vn/a-hau-huong-ly-len-tieng-ve-tin-don-hen-ho-chu-tich-miss-universe-vietnam-185240922162940951.htm\" target=\"_blank\" rel=\"noopener\">Miss Universe</a>&nbsp;2024, Hoa hậu Nguyễn Cao Kỳ Duy&ecirc;n cho ra mắt mini photobook mang t&ecirc;n&nbsp;<em>Đ&atilde; học được c&aacute;ch tha thứ cho ch&iacute;nh m&igrave;nh</em>. Song t&aacute;c phẩm n&agrave;y sau đ&oacute; lại vấp phải những &yacute; kiến tr&aacute;i chiều tr&ecirc;n&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Bộ Văn h&oacute;a phản hồi việc nghệ sĩ đưa clip phản cảm l&ecirc;n mạng\" href=\"https://thanhnien.vn/bo-van-hoa-phan-hoi-viec-nghe-si-dua-clip-phan-cam-len-mang-185240907184829672.htm\" target=\"_blank\" rel=\"noopener\">mạng x&atilde; hội</a>. C&oacute; &yacute; kiến cho rằng s&aacute;ch xuất hiện nhiều lỗi ch&iacute;nh tả, lỗi tr&igrave;nh b&agrave;y cũng như nội dung thiếu chiều s&acirc;u. Chưa dừng lại ở đ&oacute;, nhiều người mỉa mai n&agrave;ng hậu 9X bằng c&aacute;ch khơi lại ph&aacute;t ng&ocirc;n &ldquo;chưa đọc hết một cuốn s&aacute;ch&rdquo; m&agrave; c&ocirc; từng chia sẻ.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/ky-duyen-17332213938711920197100.png\" alt=\"\" width=\"640\" height=\"427\" />\r\n<figcaption>\r\n<p class=\"\" data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">Hoa hậu Kỳ Duy&ecirc;n trong buổi k&yacute; tặng s&aacute;ch&nbsp;<em>Đ&atilde; học được c&aacute;ch tha thứ cho ch&iacute;nh m&igrave;nh</em></p>\r\n</figcaption>\r\n</figure>\r\n<p>Khi vụ việc xảy ra, nhiều cư d&acirc;n mạng đặt vấn đề về tr&aacute;ch nhiệm của nh&agrave; xuất bản khi để cuốn mini photobook c&oacute; nhiều lỗi nhưng vẫn được ph&aacute;t h&agrave;nh. Một t&agrave;i khoản b&igrave;nh luận: &ldquo;Rồi nh&agrave; xuất bản n&agrave;o đọc xong kh&ocirc;ng th&egrave;m kiểm tra lại &agrave;, cứ thế xuất bản rồi b&aacute;n th&ocirc;i?&rdquo;. Người xem kh&aacute;c thắc mắc: &ldquo;Rồi kh&ocirc;ng c&oacute; ai chỉnh sửa &agrave;? Ẩu từ kh&acirc;u bi&ecirc;n tập đến kh&acirc;u xuất bản&rdquo;. Một người d&ugrave;ng chia sẻ: &ldquo;Cho t&ocirc;i hỏi nh&agrave; xuất bản n&agrave;o nhận in quyển n&agrave;y vậy&rdquo;. Một cư d&acirc;n mạng b&igrave;nh luận: &ldquo;Kh&ocirc;ng thể hiểu nổi lu&ocirc;n, ai duyệt cho cuốn s&aacute;ch n&agrave;y in vậy. Để in lỗi cơ bản thế n&agrave;y đ&acirc;y, mang tiếng qu&aacute;&rdquo;.</p>\r\n<p>Li&ecirc;n quan đến vụ việc,&nbsp;<a class=\"seo-suggest-link link-inline-content\" title=\"Nguyễn Trần Trung Qu&acirc;n ra sao sau vụ m&eacute;o miệng, liệt d&acirc;y thần kinh số 7?\" href=\"https://thanhnien.vn/nguyen-tran-trung-quan-ra-sao-sau-vu-meo-mieng-liet-day-than-kinh-so-7-185240514194347806.htm\" target=\"_blank\" rel=\"noopener\">Dược sĩ Tiến</a>&nbsp;- người bi&ecirc;n soạn cuốn s&aacute;ch gửi lời xin lỗi đến kh&aacute;n giả v&agrave; Hoa hậu Kỳ Duy&ecirc;n. Anh cho rằng Miss Universe Vietnam 2024 đ&atilde; l&agrave;m tốt vai tr&ograve; cung cấp &yacute; tưởng v&agrave; tư liệu đầy đủ trong thời gian ngắn, c&ograve;n sơ suất trong qu&aacute; tr&igrave;nh kiểm duyệt thuộc về m&igrave;nh.</p>\r\n<p>&ldquo;Nhiệm vụ của t&ocirc;i l&agrave; bi&ecirc;n soạn lại c&acirc;u chữ, kiểm tra về ch&iacute;nh tả đối với bản tiếng Việt v&agrave; ngữ ph&aacute;p với bản tiếng Anh. Thế n&ecirc;n Tiến muốn gửi lời xin lỗi ch&acirc;n th&agrave;nh đến Hoa hậu Kỳ Duy&ecirc;n, v&igrave; để sơ suất của Tiến khiến Duy&ecirc;n bị ảnh hưởng. C&ugrave;ng với đ&oacute;, Tiến cũng gửi lời xin lỗi đến những độc giả đ&atilde; v&agrave; sẽ sở hữu cuốn mini photobook n&agrave;y, v&igrave; sự thiếu cẩn thận trong qu&aacute; tr&igrave;nh kiểm duyệt cuối c&ugrave;ng trước khi ph&aacute;t h&agrave;nh&rdquo;, anh chia sẻ.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/ky-duyen-948-1733221393790678686.png\" alt=\"\" width=\"640\" height=\"952\" />\r\n<figcaption>\r\n<p data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">Mini photobook của Kỳ Duy&ecirc;n nằm trong chiến dịch từ thiện g&acirc;y quỹ cho trẻ em v&ugrave;ng cao</p>\r\n</figcaption>\r\n</figure>\r\n<p>Dược sĩ Tiến cho biết đang chuẩn bị cho việc t&aacute;i bản cuốn mini photobook n&agrave;y trong thời gian tới. Anh khẳng định sẽ kiểm duyệt lại kỹ c&agrave;ng c&aacute;c vấn đề về ch&iacute;nh tả, cũng như ho&agrave;n thiện những chi tiết được th&ecirc;m v&agrave;o về h&agrave;nh tr&igrave;nh của Kỳ Duy&ecirc;n tại Mexico. &ldquo;Tiến cam kết rằng t&aacute;i bản của cuốn mini photobook n&agrave;y sẽ được chỉn chu nhất c&oacute; thể về mặt c&acirc;u chữ v&agrave; h&agrave;nh văn, để mang đến cho qu&yacute; độc giả trải nghiệm tuyệt vời khi thưởng thức s&aacute;ch&rdquo;, anh b&agrave;y tỏ.</p>\r\n<p>Về phần Nguyễn Cao Kỳ Duy&ecirc;n, c&ocirc; gửi lời xin lỗi v&igrave; sự thiếu s&oacute;t, đồng thời mong kh&aacute;n giả tiếp tục ủng hộ dự &aacute;n n&agrave;y. &ldquo;Thời gian ph&aacute;t h&agrave;nh bản t&aacute;i bản sẽ được cập nhật một c&aacute;ch rộng r&atilde;i sớm nhất. Qu&yacute; độc giả n&agrave;o đ&atilde; sở hữu bản đầu ti&ecirc;n, thời điểm đ&oacute; c&oacute; thể đến c&aacute;c điểm nh&agrave; s&aacute;ch của Fahasa để đổi lấy bản cập nhật mới&rdquo;, Miss Universe Vietnam 2024 cho hay.</p>\r\n<p>Trước đ&oacute;, khi n&oacute;i về dự &aacute;n&nbsp;<em>Đ&atilde; học được c&aacute;ch tha thứ cho ch&iacute;nh m&igrave;nh</em>, Kỳ Duy&ecirc;n chia sẻ c&ocirc; mong muốn chia sẻ với mọi người về c&acirc;u chuyện m&igrave;nh đ&atilde; trải qua trong 10 năm, từ sau khi đăng quang Hoa hậu Việt Nam 2014 đến khi gi&agrave;nh vương miện Miss Universe Vietnam 2024. &ldquo;Duy&ecirc;n đ&atilde; học được c&aacute;ch tha thứ cho bản th&acirc;n sau nhiều chuyện ti&ecirc;u cực về tinh thần. Duy&ecirc;n biết việc ph&aacute;t h&agrave;nh mini photobook kh&ocirc;ng phải qu&aacute; to t&aacute;t, chỉ l&agrave; Duy&ecirc;n muốn truyền tải c&acirc;u chuyện của bản th&acirc;n, mong n&oacute; tới được nhiều người hơn th&ocirc;ng qua h&igrave;nh ảnh v&agrave; c&acirc;u chữ để ch&uacute;ng ta lan tỏa th&ocirc;ng điệp t&iacute;ch cực n&agrave;y&rdquo;, người đẹp cho hay.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>\r\n</div>\r\n</div>', 'ACTIVE', '2024-12-03 14:36:34', '2024-12-03 14:36:34', 9, 19),
(16, 'Ăn nghệ có tác dụng cải thiện trí nhớ?', 'an-nghe-co-tac-dung-cai-thien-tri-nho', 'cu-nghe-co-nhieu-cong-dung-tot-t.png', '<h2 class=\"detail-sapo has-audio\" data-role=\"sapo\">Nhiều gia đ&igrave;nh đ&atilde; v&agrave; đang sử dụng nghệ như một gia vị thường ng&agrave;y trong c&aacute;c bữa ăn, nhưng kh&ocirc;ng phải ai cũng biết những c&ocirc;ng dụng tuyệt vời sau đ&acirc;y của nghệ với sức khỏe, nhất l&agrave; cho tr&iacute; n&atilde;o.</h2>\r\n<p>&nbsp;</p>\r\n<p>Nghệ c&oacute; t&aacute;c dụng l&agrave;m giảm c&aacute;c nguy cơ mắc&nbsp;<a class=\"link-inline-content\" title=\"bệnh l&yacute; tim mạch\" href=\"https://thanhnien.vn/phau-thuat-cuu-nguoi-dan-ong-mac-nhieu-benh-ly-tim-mach-nang-18523062815540314.htm\" target=\"_blank\" rel=\"noopener\" data-rel=\"follow\">bệnh l&yacute; tim mạch</a>, ổn định nhịp tim, ngăn ngừa h&igrave;nh th&agrave;nh cục m&aacute;u đ&ocirc;ng v&agrave; giảm lượng đường hay mỡ xấu trong m&aacute;u. Ngo&agrave;i ra, loại củ phổ biến n&agrave;y c&ograve;n c&oacute; khả năng chống vi&ecirc;m, giảm đau do bệnh vi&ecirc;m khớp, chống oxy h&oacute;a, bảo vệ ni&ecirc;m mạc dạ d&agrave;y v&agrave; thậm ch&iacute; l&agrave; giảm nguy cơ ung thư. Một số nghi&ecirc;n cứu cũng cho thấy&nbsp;<a class=\"link-inline-content\" title=\"nghệ\" href=\"https://thanhnien.vn/bac-si-khuyen-vai-lat-nghe-moi-ngay-ngua-duoc-nhieu-benh-tat-185241122165126296.htm\" target=\"_blank\" rel=\"noopener\" data-rel=\"follow\">nghệ</a>&nbsp;c&ograve;n giảm c&aacute;c triệu chứng trầm cảm v&agrave; lo &acirc;u hiệu quả.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/cu-nghe-co-nhieu-cong-dung-tot-t.png\" alt=\"\" width=\"640\" height=\"480\" />\r\n<figcaption>\r\n<p data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">Củ nghệ c&oacute; nhiều c&ocirc;ng dụng tốt, thường được d&ugrave;ng l&agrave;m gia vị hay chế biến c&ugrave;ng c&aacute;c</p>\r\n<p data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">m&oacute;n ăn thường ng&agrave;y</p>\r\n&nbsp;</figcaption>\r\n</figure>\r\n<h2><span>Hỗ trợ tr&iacute; nhớ, ngăn ngừa ung thư</span></h2>\r\n<p>B&aacute;c sĩ chuy&ecirc;n khoa 1 Trương L&ecirc; Luy Na, Ph&oacute; trưởng khoa Dinh Dưỡng, Bệnh viện Bệnh Nhiệt đới (TP.HCM) cho biết, trong nghệ c&oacute; chứa chất&nbsp;<a class=\"link-inline-content\" title=\"curcumin\" href=\"https://thanhnien.vn/mot-phu-nu-chien-thang-ung-thu-mau-nho-dung-curcumin-co-trong-nghe-185723718.htm\" target=\"_blank\" rel=\"noopener\" data-rel=\"follow\">curcumin</a>, gi&uacute;p cải thiện tr&iacute; nhớ, tốt cho chức năng nhận thức của những người cao tuổi.</p>\r\n<p>Ngay cả ở tuổi trưởng th&agrave;nh, c&aacute;c tế b&agrave;o thần kinh n&atilde;o vẫn c&oacute; khả năng h&igrave;nh th&agrave;nh c&aacute;c kết nối mới ở một số v&ugrave;ng nhất định của n&atilde;o, ch&uacute;ng c&oacute; thể sinh s&ocirc;i v&agrave; tăng số lượng. Một trong những động lực ch&iacute;nh của qu&aacute; tr&igrave;nh n&agrave;y l&agrave; yếu tố thần kinh c&oacute; nguồn gốc từ n&atilde;o (BDNF), đ&oacute;ng vai tr&ograve; trong tr&iacute; nhớ v&agrave; khả năng học tập, c&oacute; thể được t&igrave;m thấy ở những v&ugrave;ng n&atilde;o chịu tr&aacute;ch nhiệm về ăn uống v&agrave; c&acirc;n nặng cơ thể.</p>\r\n<p>Nhiều rối loạn n&atilde;o phổ biến c&oacute; li&ecirc;n quan đến giảm mức protein BDNF, bao gồm bệnh trầm cảm v&agrave; bệnh Alzheimer. C&aacute;c nghi&ecirc;n cứu đ&atilde; ph&aacute;t hiện ra rằng curcumin trong nghệ c&oacute; thể l&agrave;m tăng mức BDNF, hiệu quả trong việc tr&igrave; ho&atilde;n hoặc thậm ch&iacute; đảo ngược tiến tr&igrave;nh ph&aacute;t triển của nhiều bệnh về n&atilde;o cũng như t&igrave;nh trạng suy giảm chức năng n&atilde;o li&ecirc;n quan đến tuổi t&aacute;c, theo chuy&ecirc;n trang sức khỏe&nbsp;<em>Healthline&nbsp;</em>(Mỹ).</p>\r\n<p>B&ecirc;n cạnh đ&oacute;, nhiều dạng ung thư kh&aacute;c nhau c&oacute; vẻ như cũng bị ảnh hưởng bởi c&aacute;c chất bổ sung curcumin. Tr&ecirc;n thực tế, curcumin đ&atilde; được nghi&ecirc;n cứu như một loại thảo mộc c&oacute; lợi trong điều trị ung thư. C&aacute;c nghi&ecirc;n cứu đ&atilde; chỉ ra rằng n&oacute; c&oacute; thể g&oacute;p phần v&agrave;o việc ti&ecirc;u diệt c&aacute;c tế b&agrave;o ung thư; giảm sự h&igrave;nh th&agrave;nh mạch m&aacute;u (sự ph&aacute;t triển của c&aacute;c mạch m&aacute;u mới trong khối u); giảm di căn.</p>\r\n<p>Ngo&agrave;i ra c&ograve;n c&oacute; bằng chứng cho thấy curcumin c&oacute; thể ngăn ngừa ung thư xảy ra ngay từ đầu, đặc biệt l&agrave; c&aacute;c bệnh ung thư hệ ti&ecirc;u h&oacute;a như ung thư đại trực tr&agrave;ng. Đặc biệt, th&ocirc;ng tin từ&nbsp;<em>Healthline</em>&nbsp;cho rằng sẽ hữu &iacute;ch hơn khi kết hợp d&ugrave;ng nghệ với&nbsp;<a href=\"https://www.healthline.com/nutrition/turmeric-and-black-pepper\" target=\"_blank\" rel=\"nofollow noopener\">hạt ti&ecirc;u đen</a>&nbsp;v&igrave; trong ti&ecirc;u c&oacute; chứa piperine, một chất tự nhi&ecirc;n gi&uacute;p tăng cường hấp thụ curcumin c&oacute; trong nghệ.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/neu-khong-dieu-tri-benh-ly-gi-da.png\" alt=\"\" width=\"640\" height=\"480\" />\r\n<figcaption>B&aacute;c sĩ Luy Na khuy&ecirc;n: Nếu kh&ocirc;ng điều trị bệnh l&yacute; g&igrave; đặc biệt, mọi người n&ecirc;n d&ugrave;ng nghệ<br />như gia vị th&ocirc;ng thường, tr&aacute;nh tự &yacute; d&ugrave;ng qu&aacute; nhiều v&igrave; c&oacute; thể g&acirc;y nhiều bất lợi cho<br />sức khỏe</figcaption>\r\n</figure>\r\n<h2>Người thiếu m&aacute;u, hay d&ugrave;ng thuốc cần ch&uacute; &yacute; khi ăn nghệ</h2>\r\n<p>B&aacute;c sĩ Luy Na khuyến c&aacute;o, những người đang bị thiếu m&aacute;u n&ecirc;n hạn chế ăn nghệ v&igrave; curcumin trong nghệ c&oacute; khả năng li&ecirc;n kết với sắt, cản trở hấp thu sắt, l&agrave;m nặng hơn t&igrave;nh trạng thiếu m&aacute;u. Nghệ cũng c&oacute; thể tương t&aacute;c với một số loại thuốc kh&aacute;c như thuốc kh&aacute;ng sinh, kh&aacute;ng vi&ecirc;m, cần lưu &yacute; hoặc tham khảo &yacute; kiến b&aacute;c sĩ khi sử dụng nghệ với c&aacute;c thuốc tr&ecirc;n.</p>\r\n<p>&ldquo;Nếu kh&ocirc;ng c&oacute; những bệnh l&yacute; đặc biệt, mọi người n&ecirc;n d&ugrave;ng nghệ với một lượng giống như gia vị thường ng&agrave;y. Hiện nay chưa c&oacute; sự đồng thuận ch&iacute;nh thức về liều lượng nghệ hoặc curcumin hiệu quả, người d&ugrave;ng c&oacute; thể tham khảo lượng nghệ tươi mỗi ng&agrave;y c&oacute; thể d&ugrave;ng dao động từ 10 đến 60 g, tinh bột nghệ khoảng từ 1 đến 10 g tương đương nửa muỗng đến một muỗng c&agrave; ph&ecirc;. Ngo&agrave;i ra, nếu d&ugrave;ng nghệ để hỗ trợ điều trị xương khớp hay c&aacute;c bệnh về dạ d&agrave;y, người bệnh c&oacute; thể tham khảo &yacute; kiến chuy&ecirc;n m&ocirc;n của b&aacute;c sĩ để đảm bảo an to&agrave;n&rdquo;, b&aacute;c sĩ Luy Na nhấn mạnh.</p>\r\n<p>Khi d&ugrave;ng nghệ với lượng nhiều v&agrave; k&eacute;o d&agrave;i c&oacute; thể g&acirc;y những c&aacute;i t&aacute;c dụng bất lợi như đầy bụng, chướng hơi, kh&oacute; ti&ecirc;u, t&aacute;o b&oacute;n, tắc ruột, tăng men gan&hellip; V&igrave; thế, cần ăn nghệ với lượng vừa phải để kh&ocirc;ng g&acirc;y hại l&ecirc;n sức khỏe người d&ugrave;ng.</p>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>', 'ACTIVE', '2024-12-03 14:40:37', '2024-12-03 14:40:37', 7, 19),
(17, '\'Việt Nam có lực lượng công nghệ nhiều quốc gia mơ cũng không có\'', 'viet-nam-co-luc-luong-cong-nghe-nhieu-quoc-gia-mo-cung-khong-co', 'ong-binh--1733227473977649819951.png', '<h2 class=\"detail-sapo has-audio\" data-role=\"sapo\">Theo Chủ tịch HĐQT Tập đo&agrave;n FPT Trương Gia B&igrave;nh, Việt Nam đ&atilde; c&oacute; vị thế v&agrave; đang tỏa s&aacute;ng tr&ecirc;n bản đồ c&ocirc;ng nghệ&nbsp;<a class=\"VCCAutoLink link-inline-content\" title=\"thế giới\" href=\"https://thanhnien.vn/the-gioi.htm\" target=\"_blank\" rel=\"noopener\" data-id-autolink=\"7435\">thế giới</a>, \'ch&uacute;ng ta c&oacute; lực lượng c&ocirc;ng nghệ m&agrave; nhiều quốc gia mơ cũng kh&ocirc;ng c&oacute;\'.</h2>\r\n<p>&nbsp;</p>\r\n<p>Đề cập tới c&aacute;c vấn đề li&ecirc;n quan dữ liệu, tr&iacute; tuệ nh&acirc;n tạo (AI) tại Hội nghị t&aacute;c động c&ocirc;ng nghệ Việt Nam 2024 do SSI Digital tổ chức ng&agrave;y 3.12 tại&nbsp;<a class=\"VCCAutoLink link-inline-content\" title=\"H&agrave; Nội\" href=\"https://thanhnien.vn/ha-noi-tags485917.html\" target=\"_blank\" rel=\"noopener\" data-id-autolink=\"8088\">H&agrave; Nội</a>, Chủ tịch HĐQT Tập đo&agrave;n FPT&nbsp;<a class=\"link-inline-content\" title=\"Trương Gia B&igrave;nh\" href=\"https://thanhnien.vn/quan-tri-doanh-nghiep-gia-dinh-nhin-tu-quan-diem-cua-ong-truong-gia-binh-185798085.htm\" data-rel=\"follow\">Trương Gia B&igrave;nh</a>&nbsp;nhấn mạnh, thế giới đang bước v&agrave;o giai đoạn lịch sử chưa từng thấy, trở n&ecirc;n bất ổn, kh&oacute; đo&aacute;n lường, một thế giới mới đang dần hiện l&ecirc;n.</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/ong-binh--1733227473977649819951.png\" alt=\"\" width=\"640\" height=\"427\" />\r\n<figcaption>\r\n<p data-placeholder=\"Nhập ch&uacute; th&iacute;ch ảnh\">Chủ tịch HĐQT Tập đo&agrave;n FPT Trương Gia B&igrave;nh chia sẻ tại sự kiện</p>\r\n</figcaption>\r\n</figure>\r\n<p>\"Trong bối cảnh như vậy, ai cũng phải nghĩ xem sẽ ứng xử với tương lai như thế n&agrave;o.<strong>&nbsp;</strong>C&oacute; thể ch&uacute;ng ta ứng xử ở g&oacute;c độ của&nbsp;<a class=\"VCCAutoLink link-inline-content\" title=\"người lao động\" href=\"https://thanhnien.vn/thoi-su/lao-dong-viec-lam.htm\" target=\"_blank\" rel=\"noopener\" data-id-autolink=\"8941\">người lao động</a>&nbsp;m&agrave;<strong>&nbsp;</strong>75% c&ocirc;ng việc đang l&agrave;m tại Việt Nam sẽ biến mất v&agrave;o năm 2030. C&oacute; lẽ cũng phải suy nghĩ với tư c&aacute;ch của một c&ocirc;ng ty, l&agrave;m thế n&agrave;o để c&oacute; sức chống đỡ, tiếp tục kinh doanh\", &ocirc;ng B&igrave;nh đặt vấn đề.</p>\r\n<p>Theo Chủ tịch Tập đo&agrave;n FPT, Việt Nam đ&atilde; c&oacute; vị thế v&agrave; đang tỏa s&aacute;ng tr&ecirc;n bản đồ c&ocirc;ng nghệ thế giới.</p>\r\n<p>\"Ng&agrave;i Narayana Murthy, nh&agrave; s&aacute;ng lập Infosys Technologies, đ&atilde; n&oacute;i tr&ecirc;n thế giới chỉ c&oacute; Việt Nam v&agrave; Ấn Độ c&oacute; những c&ocirc;ng ty c&oacute; thể xuất khẩu phần mềm vượt 1 tỉ USD. Việt Nam đứng thứ 2 sau Ấn Độ về xuất khẩu phần mềm. Ch&uacute;ng ta c&oacute; lực lượng c&ocirc;ng nghệ m&agrave; nhiều quốc gia mơ cũng kh&ocirc;ng c&oacute;\", &ocirc;ng B&igrave;nh n&oacute;i.</p>\r\n<p>Cũng theo &ocirc;ng B&igrave;nh, kh&ocirc;ng phải ngẫu nhi&ecirc;n m&agrave; &ocirc;ng Jensen Huang, CEO Nvidia, n&oacute;i rằng Nvidia chọn Việt Nam l&agrave; \"ng&ocirc;i nh&agrave; thứ hai\" của m&igrave;nh. \"V&agrave;o kỷ nguy&ecirc;n AI, Nvidia chọn Việt Nam như ng&ocirc;i nh&agrave; của m&igrave;nh v&agrave; c&ograve;n nhiều c&ocirc;ng ty kh&aacute;c sẽ tiếp tục chọn Việt Nam l&agrave; ng&ocirc;i nh&agrave; của m&igrave;nh\", &ocirc;ng B&igrave;nh tin tưởng.</p>\r\n<p>Trong bối cảnh hiện tại, Chủ tịch&nbsp;<a class=\"link-inline-content\" title=\"Tập đo&agrave;n FPT\" href=\"https://thanhnien.vn/tap-doan-fpt-ky-thoa-thuan-hop-tac-chuyen-doi-so-voi-tinh-ba-ria-vung-tau-185241105181728375.htm\" data-rel=\"follow\">Tập đo&agrave;n FPT</a>&nbsp;cho rằng, thứ quan trọng nhất, đ&oacute;ng vai tr&ograve; trung t&acirc;m l&agrave; data (dữ liệu). Dữ liệu l&agrave; AI, l&agrave; blockchain&hellip;, tất cả đều xoay quanh dữ liệu. Dữ liệu đ&atilde; trở th&agrave;nh nhi&ecirc;n liệu quan trọng nhất cho mọi nền kinh tế.</p>\r\n<p>\"Trật tự thế giới mới được thiết lập tr&ecirc;n cơ sở quốc gia n&agrave;o nắm v&agrave; quản l&yacute; dữ liệu tốt hơn. Đ&acirc;y l&agrave; th&aacute;ch thức v&ocirc; c&ugrave;ng lớn bởi dữ liệu qu&aacute; đặc th&ugrave;, rất kh&aacute;c biệt. L&agrave;m thế n&agrave;o để c&oacute; đủ dữ liệu, dữ liệu sạch, dữ liệu c&oacute; thể d&ugrave;ng được; sở hữu dữ liệu thế n&agrave;o, bảo vệ v&agrave; ph&acirc;n phối dữ liệu ra sao&hellip; l&agrave; những vấn đề rất cần phải suy nghĩ\", &ocirc;ng B&igrave;nh nhấn mạnh.</p>\r\n<h2><strong>Doanh nghiệp blockchain mất lợi thế cạnh tranh với Th&aacute;i Lan, Singapore</strong></h2>\r\n<p>Theo &ocirc;ng Nguyễn Duy Hưng, Chủ tịch C&ocirc;ng ty CP Chứng kho&aacute;n SSI, thập kỷ qua đ&atilde; chứng kiến sự ph&aacute;t triển vượt bậc của c&ocirc;ng nghệ tr&ecirc;n to&agrave;n cầu, đặc biệt l&agrave; trong lĩnh vực t&agrave;i sản số, bao gồm blockchain, tiền m&atilde; h&oacute;a, token h&oacute;a t&agrave;i sản v&agrave; c&aacute;c ứng dụng phi tập trung (DeFi).</p>\r\n<figure class=\"image\"><img src=\"/storage/photos/19/post/ong-hung--1733227493130164373600.png\" alt=\"\" width=\"640\" height=\"427\" />\r\n<figcaption>Chủ tịch C&ocirc;ng ty CP Chứng kho&aacute;n SSI Nguyễn Duy Hưng cho rằng, Việt Nam c&oacute; nhiều<br />điều kiện thuận lợi để trở th&agrave;nh trung t&acirc;m ph&aacute;t triển t&agrave;i sản số trong khu vực</figcaption>\r\n</figure>\r\n<p>Việt Nam l&agrave; quốc gia c&oacute; d&acirc;n số trẻ, y&ecirc;u c&ocirc;ng nghệ v&agrave; sẵn s&agrave;ng thử nghiệm những xu hướng mới. Điều n&agrave;y tạo điều kiện thuận lợi để Việt Nam trở th&agrave;nh trung t&acirc;m ph&aacute;t triển t&agrave;i sản số trong khu vực. Tuy nhi&ecirc;n, thực tế cho thấy do thiếu một khung ph&aacute;p l&yacute; r&otilde; r&agrave;ng, lĩnh vực n&agrave;y vẫn đang đối mặt với nhiều th&aacute;ch thức.</p>\r\n<p>\"C&aacute;c doanh nghiệp trong lĩnh vực<a class=\"link-inline-content\" title=\" blockchain \" href=\"https://thanhnien.vn/ung-dung-blockchain-va-ai-trong-cong-tac-cong-an-tang-cuong-an-ninh-trat-tu-185241127101754441.htm\" data-rel=\"follow\">&nbsp;blockchain&nbsp;</a>v&agrave; t&agrave;i sản số tại Việt Nam vẫn phải hoạt động trong điều kiện thiếu định hướng, khiến họ mất lợi thế cạnh tranh so với c&aacute;c nước l&aacute;ng giềng như Singapore hay Th&aacute;i Lan\", &ocirc;ng Hưng n&oacute;i.</p>\r\n<p>Nhiều chuy&ecirc;n gia c&ocirc;ng nghệ chỉ ra, đối với lĩnh vực t&agrave;i ch&iacute;nh, blockchain đ&atilde; hỗ trợ giao dịch tiền m&atilde; h&oacute;a, hợp đồng th&ocirc;ng minh v&agrave; c&aacute;c dịch vụ t&agrave;i ch&iacute;nh phi tập trung m&agrave; kh&ocirc;ng cần qua trung gian truyền thống như ng&acirc;n h&agrave;ng. Điều n&agrave;y gi&uacute;p giảm chi ph&iacute; giao dịch, tăng tốc độ xử l&yacute;&hellip;</p>\r\n<p>D&ugrave; vậy, b&ecirc;n cạnh những mặt thuận lợi v&agrave; cơ hội, việc ph&aacute;t triển blockchain ở Việt Nam đang đối mặt với kh&ocirc;ng &iacute;t th&aacute;ch thức, điển h&igrave;nh như thiếu nguồn nh&acirc;n lực chất lượng cao, nhận thức của thị trường, quy định ph&aacute;p l&yacute; c&ograve;n hạn chế&hellip;</p>\r\n<figure class=\"image\">\r\n<figcaption></figcaption>\r\n</figure>\r\n<div class=\"detail-content afcbc-body\" data-role=\"content\" data-io-article-url=\"https://thanhnien.vn/viet-nam-co-luc-luong-cong-nghe-nhieu-quoc-gia-mo-cung-khong-co-18524120319185998.htm\">\r\n<figure class=\"image\"></figure>\r\n<p>&nbsp;</p>\r\n</div>', 'ACTIVE', '2024-12-03 14:44:12', '2024-12-03 14:44:12', 6, 19);

